import React, { Component } from 'react'
import { Table, Icon } from 'antd'
import moment from 'moment'

class AttendancesDetail extends Component {
  render() {
    const {
      props: { attendanceInfo, conferenceUsers }
    } = this

    const rowsData = conferenceUsers.map(({ user }, index) => ({
      key: index,
      fullName: user.firstname + ' ' + user.lastname,
      userId: user._id,
      when: user._id
    }))

    const columns = [
      {
        title: 'Họ và Tên',
        dataIndex: 'fullName',
        key: 'fullName'
      },
      {
        title: 'Điểm danh',
        dataIndex: 'userId',
        className: 'attended',
        key: 'userId',
        width: 150,
        render: userId => (
          <span>
            {attendanceInfo.userAttendances
              .filter(e => e.isAttended)
              .map(e => e.user._id)
              .includes(userId) ? (
              <Icon type='check' />
            ) : (
              <></>
            )}
          </span>
        )
      },
      {
        title: 'Thời điểm',
        dataIndex: 'when',
        key: 'when',
        width: 150,
        render: userId => {
          const userFound = attendanceInfo.userAttendances.find(e => e.user._id === userId)
          return userFound ? (
            userFound.isAttended ? (
              <span>{moment(userFound.createdAt).format('DD[/]MM[/]YYYY, HH:mm')}</span>
            ) : (
              <></>
            )
          ) : (
            <></>
          )
        }
      }
    ]

    return (
      <div className='AattendancesDetailTabelWrapper'>
        <Table size={'small'} pagination={false} columns={columns} dataSource={rowsData} rowKey='userId' />
      </div>
    )
  }
}
export default AttendancesDetail
