import { observable, action } from 'mobx'

import { LAYOUT_MODEL } from '../pages/conference2/jitsi/constants'

// import { IJitsiStore } from './jitsiStore_types'

const DEFAULT_STORE = {
  // *_: Jitsi
  connection: undefined,
  conference: undefined,

  _connection: undefined,
  _conference: undefined,

  // *_: Connection
  isConnected: false,
  isJoined: false,

  _isConnected: false,
  _isJoined: false,

  connectionStats: undefined,
  videoResolutionFromJVB: 1080,

  // *_: User's Media
  audioAccessGranted: false,
  cameraAccessGranted: false,

  isAudio: false,
  audioTrack: undefined,

  isCamera: false,
  videoTrack: undefined,

  isShareScreen: false,
  desktopTrack: undefined,

  // *_: Transcriber (Do not edit)
  isTranscribing: false,
  isDialing: false,
  isTerminating: false,
  transcriberId: null,
  potentialTranscriberIds: [],

  // *_: Participant
  participants: [],
  participantTracks: [],
  participantAudios: [],

  speaker: undefined,

  // *_: Media devices
  availableDevices: {
    audioInput: [],
    audioOutput: [],
    videoInput: []
  },

  userSelectedVideoInputDevice: undefined,

  userSelectedAudioInputDevice: undefined,
  audioInputLevel: 1,

  userSelectedAudioOutputDevice: undefined,
  audioOutputLevel: 1,

  // *_: UI
  isShareAudioInProcess: false,
  isShareCameraInProcess: false,

  isPinnedMode: false,
  lockMyVideo: false,
  layoutModel: LAYOUT_MODEL.SINGLE,
  // *: Delect whenever to hide toolbar
  isMouseMove: true,
  actionTimeout: null,

  isDisconnect: false
}

export class JitsiStore {
  // *:
  @observable connection = DEFAULT_STORE.connection
  @observable conference = DEFAULT_STORE.conference

  @observable _connection = DEFAULT_STORE._connection
  @observable _conference = DEFAULT_STORE._conference

  // *: Connection
  @observable isConnected = DEFAULT_STORE.isConnected
  @observable isJoined = DEFAULT_STORE.isJoined

  @observable _isConnected = DEFAULT_STORE._isConnected
  @observable _isJoined = DEFAULT_STORE._isJoined

  @observable connectionStats = DEFAULT_STORE.connectionStats
  @observable videoResolutionFromJVB = DEFAULT_STORE.videoResolutionFromJVB

  // *: User's Media
  @observable audioAccessGranted = DEFAULT_STORE.audioAccessGranted
  @observable cameraAccessGranted = DEFAULT_STORE.cameraAccessGranted

  @observable isAudio = DEFAULT_STORE.isAudio
  @observable audioTrack = DEFAULT_STORE.audioTrack

  @observable isCamera = DEFAULT_STORE.isCamera
  @observable videoTrack = DEFAULT_STORE.videoTrack

  @observable isShareScreen = DEFAULT_STORE.isShareScreen
  @observable desktopTrack = DEFAULT_STORE.desktopTrack

  // *: Transcriber
  @observable isTranscribing = DEFAULT_STORE.isTranscribing
  @observable isDialing = DEFAULT_STORE.isDialing
  @observable isTerminating = DEFAULT_STORE.isTerminating
  @observable transcriberId = DEFAULT_STORE.transcriberId
  @observable potentialTranscriberIds = DEFAULT_STORE.potentialTranscriberIds

  // *: Participant
  @observable participants = DEFAULT_STORE.participants
  @observable participantTracks = DEFAULT_STORE.participantTracks
  @observable participantAudios = DEFAULT_STORE.participantAudios

  @observable speaker = DEFAULT_STORE.speaker

  // *: Media devices
  @observable availableDevices = DEFAULT_STORE.availableDevices

  @observable userSelectedVideoInputDevice = DEFAULT_STORE.userSelectedVideoInputDevice

  @observable userSelectedAudioInputDevice = DEFAULT_STORE.userSelectedAudioInputDevice
  @observable audioInputLevel = DEFAULT_STORE.audioInputLevel

  @observable userSelectedAudioOutputDevice = DEFAULT_STORE.userSelectedAudioOutputDevice
  @observable audioOutputLevel = DEFAULT_STORE.audioOutputLevel

  // *: UI
  @observable isShareAudioInProcess = DEFAULT_STORE.isShareAudioInProcess
  @observable isShareCameraInProcess = DEFAULT_STORE.isShareCameraInProcess

  @observable isPinnedMode = DEFAULT_STORE.isPinnedMode
  @observable lockMyVideo = DEFAULT_STORE.lockMyVideo

  @observable layoutModel = DEFAULT_STORE.layoutModel
  @observable isMouseMove = DEFAULT_STORE.isMouseMove
  @observable actionTimeout = DEFAULT_STORE.actionTimeout

  @observable isDisconnect = DEFAULT_STORE.isDisconnect

  @action setState = (state: any) => {
    Object.keys(state).map(key => {
      this[key] = state[key]
      return key
    })
  }

  @action resetStore = () => {
    Object.keys(DEFAULT_STORE).map(key => {
      this[key] = DEFAULT_STORE[key]
      return key
    })
  }
}
