import React from 'react'
import { withTranslation } from 'react-i18next'
import { Modal, Button, Form, Input } from 'antd'

import { client, M_CREATE_SERVER } from '@graphql'
import { returnError, newNotification } from '@utils'

class ModalAddServer extends React.PureComponent {
  state = { visible: false }

  showModal = () => {
    this.setState({
      visible: true
    })
  }

  handleCancel = e => {
    this.setState({
      visible: false
    })
  }

  mutationCreateServer = record =>
    client
      .mutate({ mutation: M_CREATE_SERVER, variables: { newServer: record } })
      .then(({ data: { createServer } }) => {
        if (createServer) {
          this.props.queryServer()
          newNotification(this.props.i18n.t('notification.createServerSuccess'), 'success')
        }
      })
      .catch(error => {
        returnError('CreateServer', { error, errorMessage: this.props.i18n.t('notification.createServerError') })
      })

  handleSubmit = e => {
    e.preventDefault()
    this.props.form.validateFields((err, { domain, muc, weightFrom, weightTo, ...rest }) => {
      if (!err) {
        if (+weightFrom >= +weightTo) return newNotification('Invalid `weightFrom` or `weightTo`')

        this.mutationCreateServer({
          ...rest,
          hosts: {
            domain,
            muc
          },
          range: [+weightFrom, +weightTo]
        })

        this.setState({ visible: false })
      }
    })
  }

  render() {
    const { 
      form: { getFieldDecorator},
      i18n
    } = this.props

    return (
      <>
        <Button type='primary' style={{ marginBottom: 10 }} icon='plus' onClick={this.showModal}>
          {i18n.t('common.add')}
        </Button>
        <Modal
          title={i18n.t('setting.titleCreate')}
          visible={this.state.visible}
          onOk={this.handleSubmit}
          onCancel={this.handleCancel}
          cancelText={i18n.t('common.cancel')}
          okText={i18n.t('common.add')}
        >
          <Form labelCol={{ span: 5 }} wrapperCol={{ span: 12 }} onSubmit={this.handleSubmit}>
            <Form.Item label={i18n.t('setting.code')}>
              {getFieldDecorator('code', {
                rules: [{ required: true, message: i18n.t('require.requireServerCode') }]
              })(<Input />)}
            </Form.Item>

            <Form.Item label={i18n.t('setting.name')}>
              {getFieldDecorator('name', {
                rules: [{ required: true, message: i18n.t('require.requireServerName') }]
              })(<Input />)}
            </Form.Item>

            <Form.Item label={i18n.t('setting.domain')}>
              {getFieldDecorator('domain', {
                rules: [{ required: true, message: i18n.t('require.requireServerDomain') }]
              })(<Input />)}
            </Form.Item>

            <Form.Item label='Muc'>
              {getFieldDecorator('muc', {
                rules: [{ required: true, message: i18n.t('require.requireServerMuc') }]
              })(<Input />)}
            </Form.Item>

            <Form.Item label='Bosh'>
              {getFieldDecorator('bosh', {
                rules: [{ required: true, message: i18n.t('require.requireServerBosh') }]
              })(<Input />)}
            </Form.Item>

            <Form.Item label='Weight From'>
              {getFieldDecorator('weightFrom', {
                rules: [{ required: true, message: i18n.t('require.requireServerWeightFrom') }]
              })(<Input type='number' min='0' />)}
            </Form.Item>

            <Form.Item label='Weight To'>
              {getFieldDecorator('weightTo', {
                rules: [{ required: true, message: i18n.t('require.requireServerWeightTo') }]
              })(<Input type='number' min='0' />)}
            </Form.Item>
          </Form>
        </Modal>
      </>
    )
  }
}

export default withTranslation()(Form.create()(ModalAddServer))
