import React from 'react'
import { Table } from 'antd'
import { withTranslation } from 'react-i18next'
import ModalAssignRole from './ModalEditRole/ModalAssignRole'
import ModalEditUserInNode from './ModalEditUserInNode/ModalEditUserInNode'

class ListAdminNode extends React.PureComponent {
  render() {
    const {
      props: {
        admins,
        i18n,
        node,
        queryNode,
        isAllowEditUserInNode,
        isAllowAssignRole
      }
    } = this

    const columns = [
      {
        title: i18n.t('user.username'),
        dataIndex: 'username'
      },
      {
        title: i18n.t('common.name'),
        render: user => `${user.firstname} ${user.lastname}`
      },
      {
        title: 'Email',
        dataIndex: 'email'
      },
      {
        title: i18n.t('role.role'),
        render: user => user.profiles?.find(profile => profile?.node?._id === node._id)?.role?.name
      },
      {
        render: (_, user) => (
          <span
            className='columnBtnHolder'
          >
            {isAllowEditUserInNode && (
              <ModalEditUserInNode
                user={user}
                node={node}
                queryNode={queryNode}
              />
            )}

            {isAllowAssignRole && (
              <ModalAssignRole
                labelButton={i18n.t('group.demote')}
                user={user}
                node={node}
                queryNode={queryNode}
              />
            )}

          </span>
        )
      }
    ]

    return (
      <div className='detail-group-item'>
        <div className='header'>
          <h3>{i18n.t('group.groupAdmin')}</h3>
          <div className='group-button'>

          </div>
        </div>
        <div className='content'>
          {admins && admins.length > 0 ? (
            <div className='wrapper-table'>
              <p className='no-data'>{i18n.t('group.titleGroupAdmin')}</p>
              <Table
                className='common-table'
                columns={columns}
                dataSource={admins}
              />
            </div>
          ) : (
              <p className='no-data'>{i18n.t('group.noDataAdmin')}</p>
            )}
        </div>
      </div>
    )
  }
}

export default withTranslation()(ListAdminNode)
