import React from 'react'
import { Button } from 'antd'
import { inject, observer } from 'mobx-react'

import { client, M_SEND_EMAIL } from '@graphql'

import InputInvite from './InputInvite'

import successIcon from '@misc/images/toolbar/success.svg'
import errorIcon from '@misc/images/toolbar/error.svg'

const STATUS = {
  INVITE: 'INVITE',
  SENDING: 'SENDING',
  SENT: {
    SUCCESS: 'SUCCESS',
    ERROR: 'ERROR'
  }
}

@inject(({ stores }) => stores)
@observer
class TabInvite extends React.PureComponent {
  state = {
    data: [],
    status: STATUS.INVITE
  }

  handleSendEmail = () => {
    this.setState(_preState => ({
      ..._preState,
      status: STATUS.SENDING
    }))

    const emailValidateTrue = this.state.data.filter(item => item.validate).map(item => item.value)
    client
      .mutate({
        mutation: M_SEND_EMAIL,
        variables: {
          typeSend: 'INVITE',
          typeEmail: 'JOIN_CONFERENCE',
          conferenceId: this.props.conferenceState.conference._id,
          emails: emailValidateTrue
        }
      })
      .then(({ data: { sendEmail } }) => {
        this.setState(_preState => ({
          ..._preState,
          status: STATUS.SENT.SUCCESS
        }))
      })
      .catch(error => {
        this.setState(_preState => ({
          ..._preState,
          status: STATUS.SENT.ERROR
        }))
      })
  }

  handleDeleteItem = (item) => {
    this.setState(_preState => ({
      data: [..._preState.data.slice(0, item), ..._preState.data.slice(item + 1, _preState.data.length)]
    }))
  }

  handleAddItem = (item) => {
    if(!this.state.data.filter(e => e.value === item.value).length) {
      this.setState(_preState => ({
        ..._preState,
        data: [..._preState.data, ...item]
      }))
    }
  }

  handleResetInvite = () => {
    this.setState(_preState => ({
      ..._preState,
      status: STATUS.INVITE,
      data: []
    }))
  }

  render() {
    const countEmail = this.state.data.filter(item => item.validate).map(item => item.value).length
    const hasEmailValidate = countEmail > 0

    const renderElement = {
      [STATUS.INVITE]: (
        <div className='box-invite'>
          <p>Invitees:</p>
          <InputInvite
            data={this.state.data}
            handleAddItem={this.handleAddItem}
            handleDeleteItem={this.handleDeleteItem}
          />
          <p>Separate addresses with commas</p>
          <Button type='primary' disabled={!hasEmailValidate} onClick={this.handleSendEmail}>Send</Button>
        </div>
      ),
      [STATUS.SENDING]: (
        <div className='box-loading'>
          <div className='spinner'></div>
          <p>Sending Invation</p>
        </div>
      ),
      [STATUS.SENT.SUCCESS]: (
        <div className='box-sent'>
          <img src={successIcon} alt='icon' />
          <span className='success'>{countEmail} Invitation was sent</span>
          <div className='group-btn'>
            <Button type='primary' onClick={this.handleResetInvite}>Invite more</Button>
            <Button type='primary' ghost onClick={this.props.handleCancel}>Done</Button>
          </div>
        </div>
      ),
      [STATUS.SENT.ERROR]: (
        <div className='box-sent'>
          <img src={errorIcon} alt='icon' />
          <span className='error'>{countEmail} Invitation was not sent</span>
          <div className='group-btn'>
            <Button type='primary' onClick={this.handleResetInvite}>Invite more</Button>
            <Button type='primary' ghost onClick={this.props.handleCancel}>Done</Button>
          </div>
        </div>
      ),
    }
    return (
      <div className='wrapper-invite'>
        {
          renderElement[this.state.status]
        }
      </div>
    )
  }
}

export default TabInvite
