import React from 'react'
import { withTranslation } from 'react-i18next'
import { Form, Input, Button } from 'antd'

class CreateUserForm extends React.Component {
  handleSubmit = e => {
    e.preventDefault();
    this.props.form.validateFields((err, values) => {
      if (!err) {
        this.props.handleAnonymousJoinConference(values)
      }
    });
  };

  render() {
    const {
      form: { getFieldDecorator },
      i18n
    } = this.props

    return (
      <Form onSubmit={this.handleSubmit} className="join-conference-form">
        <p>{i18n.t('require.pleaseEnterInfo')}</p>
        <Form.Item>
          {getFieldDecorator('firstname', {
            rules: [{ required: true, message: i18n.t('require.requireFirstname') }],
          })(<Input placeholder={i18n.t('user.firstname')} />)}
        </Form.Item>
        <Form.Item>
          {getFieldDecorator('lastname', {
            rules: [{ required: true, message: i18n.t('require.requireLastname') }],
          })(<Input placeholder={i18n.t('user.lastname')} />)}
        </Form.Item>
        <Form.Item>
          <Button type="primary" htmlType="submit" className="login-form-button">
            {i18n.t('verify.joinMeeting')}
          </Button>
        </Form.Item>
      </Form>
    );
  }
}

export default withTranslation()(Form.create({ name: 'normal_login' })(CreateUserForm))
