import React from 'react'

class RightMessage extends React.PureComponent {
  renderMessage = () => {
    const { data } = this.props
    let renderMessage

    if(!data || !data.messages) return <React.Fragment />

    switch (data.messages.length) {
      case 1:
        {
          renderMessage = data.messages.map((item, index) => {
            return (
              <div key={index}>
                <span className='message-single'>{item}</span>
                <div className='clear'></div>
              </div>
            )
          })
          break;
        }
      case 2:
        {
          renderMessage = data.messages.map((item, index) => {
            if (index === 0) {
              return (
                <div key={index}>
                  <span className='message-top'>{item}</span>
                  <div className='clear'></div>
                </div>
              )
            }
            if (index === 1) {
              return (
                <div key={index}>
                  <span className='message-bottom'>{item}</span>
                  <div className='clear'></div>
                </div>
              )
            }
            return item
          })
          break;
        }

      default:
        {
          renderMessage = data.messages.map((item, index) => {
            if (index === 0) {
              return (
                <div key={index}>
                  <span className='message-top'>{item}</span>
                  <div className='clear'></div>
                </div>
              )
            }
            if (index === data.messages.length - 1) {
              return (
                <div key={index}>
                  <span className='message-bottom'>{item}</span>
                  <div className='clear'></div>
                </div>
              )
            }
            return (
              <div key={index}>
                <span className='message-center'>{item}</span>
                <div className='clear'></div>
              </div>
            )
          })
          break;
        }
    }

    return renderMessage
  }


  render() {

    return (
      <div className='right-message'>
        <div className='content-message'>
          {
            this.renderMessage()
          }
          <div className='clear'></div>
        </div>
      </div>
    )
  }
}

export default RightMessage
