import React, { Component } from 'react'
import { Progress } from 'antd'
import { inject, observer } from 'mobx-react'
// import { checkPermission } from '@constants'
// import { newNotification } from '@utils'

@inject(({ stores }) => stores)
@observer
class ReportView extends Component {
  handleMapData = e => {
    const userAns = [...e.userVotes].map(e => e.key)
    const allAns = [...e.answers].map(e => ({
      ...e,
      totalAmnt: userAns.length,
      amnt: userAns.filter(k => k === e.key).length
    }))
    return allAns
  }

  render() {
    const {
      props: {
        conferenceState: { donePolls }
      }
    } = this
    return (
      <div className='reportWrapper'>
        {donePolls.map(e => (
          <div key={e._id} className='reportHolder'>
            <div className='question'>
              <div className='questionHolder'>
                <span> Q: {e.question}</span>
                {/* {checkPermission(conferenceState.myPermissions, 'VOTE_CREATE') && (
                  <span className='publicToggle'>
                    Public:
                    <Checkbox defaultChecked={e.isPublic} value={e._id} onChange={this.handleTogglePublic}></Checkbox>
                  </span>
                )} */}
              </div>

              <div className='answerHolder'>
                {this.handleMapData(e).map(k => (
                  <div className='answerContent' key={k.key}>
                    {k.key}. {k.value} - {k.amnt}/{k.totalAmnt}
                    <div className='percentShow'>
                      <span>
                        <Progress status='normal' percent={(k.amnt / k.totalAmnt) * 100} />
                      </span>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          </div>
        ))}
      </div>
    )
  }
}
export default ReportView
