import React from 'react'
import { withTranslation } from 'react-i18next'
import { Form, Input, Button } from 'antd'

class ForgotPasswordForm extends React.Component {
  handleSubmit = e => {
    e.preventDefault()
    this.props.form.validateFields((err, values) => {
      if (!err) {
        this.props.handleForgotPassword(values.email)
      }
    })
  }

  render() {
    const {
      form: { getFieldDecorator },
      i18n
    } = this.props

    return (
      <Form onSubmit={this.handleSubmit} className='join-conference-form'>
        <Form.Item>
          {getFieldDecorator('email', {
            rules: [
              {
                type: 'email',
                message: i18n.t('require.requireEmailCorrect')
              },
              { required: true, message: i18n.t('require.pleaseEnterEmail') }
            ]
          })(<Input placeholder={i18n.t('forgotPassword.emailAddress')} />)}
        </Form.Item>
        <Form.Item>
          <Button type='primary' htmlType='submit' className='login-form-button'>
            {i18n.t('common.save')}
          </Button>
        </Form.Item>
      </Form>
    )
  }
}

export default withTranslation()(Form.create({ name: 'fofgot_password' })(ForgotPasswordForm))
