import React from 'react'
import { withTranslation } from 'react-i18next'
import { Modal, Button, Tooltip } from 'antd'
import './ButtonMoreNodes.less'
import { withRouter, Link } from 'react-router-dom'

@withRouter
class ButtonMoreNodes extends React.PureComponent {
  state = { visible: false }

  showModal = () => {
    this.setState({ visible: true })
  }

  handleCancel = () => {
    this.setState({ visible: false })
  }

  render() {
    const {
      i18n,
      profiles
    } = this.props

    let nodes = profiles?.filter(pro => pro?.node).map(pro => <p className='tooltip-node-name'>{pro.node?.name}</p>)
    if (nodes?.length > 5) {
      nodes = [...nodes.slice(0, 5), <p>...</p>]
    }

    return (
      <>
        <Tooltip
          placement="top"
          title={nodes?.length ? nodes : i18n.t('user.notBelongToAnyGroup')}
        >
          <Link
            to={null}
            onClick={this.showModal}
          >
            {i18n.t('user.seeItAll')
            }</Link>
        </Tooltip>

        <Modal
          className='common-modal'
          title={i18n.t('user.allNodes')}
          visible={this.state.visible}
          footer={null}
          onCancel={this.handleCancel}
          centered
        >
          <div className='wrapper-nodes'>
            {nodes?.length ?
              (profiles?.map((profile, index) => !!(profile?.node?.name) &&
                (
                  <div key={index} className='node-more-item'>
                    <Link to={`/groupDetail/${profile?.node?._id}`}>{profile?.node?.name}</Link>
                  </div>
                ))) : i18n.t('user.notBelongToAnyGroup')
            }
          </div>
          <div className='wrapper-button'>
            <Button type='primary' onClick={this.handleCancel}>Ok</Button>
            <Button type='primary' ghost onClick={this.handleCancel}>
              {i18n.t('common.cancel')}
            </Button>
          </div>
        </Modal>
      </>
    )
  }
}

export default withTranslation()(ButtonMoreNodes)
