import React from 'react'
import { Icon, Progress, Button } from 'antd'
import './PollsTable.less'

class QuestionPollItem extends React.PureComponent {
  state = {
    visible: false
  }

  handleChangeVisible = () => {
    this.setState(_preState => ({
      visible: !_preState.visible
    }))
  }

  render() {
    return (
      <div className='poll-item'>
        <div className='poll-item-summary' onClick={this.handleChangeVisible}>
          <span className='title'>
            <Icon
              type='caret-down'
              style={{ transform: `rotate(${this.state.visible ? 180 : 0}deg)`, transition: 'all .5s' }}
            />
            {this.props.dataPoll.name}</span>
          <span className='total-question'>{`${this.props.dataPoll.questions.length} Question`}</span>
          <span className='anonymous'>none</span>
          <div className='status'>
            <span className='columnBtnHolder'>
              <Button className='btn-start'>Edit</Button>
              <Button className='btn-delete'>Delete</Button>
            </span>
            {/* <span className='launch'>Polling in progess</span>
            <span className='end'>Poll is ended</span> */}
          </div>
        </div>

        {
          this.state.visible ? (
            <div className='poll-item-detail'>
              {
                this.props.dataPoll.questions.map((item, index) => {
                  return (
                    <div key={index} className='question-poll-item'>
                      <p className='question-poll-item-header'>{`Question : ${item.question} (${item.type} Choice)`}</p>
                      <div className='question-poll-item-body'>
                        {
                          item.answers.map((item1, index1) => {
                            return (
                              <div  key={index1} className='answer-poll-item'>
                                <p>{`Answer ${index1}: ${item1.value}`}</p>
                                <Progress percent={50} size="small" status="active" />
                              </div>
                            )
                          })
                        }
                      </div>
                    </div>
                  )
                })
              }
            </div>
          ) : null
        }
      </div>
    )
  }
}

export default QuestionPollItem
