import React from 'react'
import { Link, NavLink } from 'react-router-dom'
import { withTranslation } from 'react-i18next'
import { withRouter } from 'react-router-dom'
import { Switch } from 'antd'
import { inject, observer } from 'mobx-react'
import ReactNotification from 'react-notifications-component'
import './index.less'

import { PER } from '@constants'
import { AvatarComponent } from '@components'

import googleCalendarAPI from '../../calendar/GoogleCalendarAPI'
import i18next from '../../translation/index'
import logo from '../../misc/logo/asset9.svg'
import { isSupperAdmin, hasPermission } from '@utils'
@withRouter
@inject(({ stores }) => stores)
@observer
class LayoutDashboard extends React.PureComponent {
  constructor(props) {
    super(props)
    this.state = {
      currentState: '',
      checked: true
    }
  }

  componentDidMount() {
    this.setState({ checked: localStorage.getItem('i18nextLng') === 'vi' })
  }

  handleLogout = () => {
    // Google API
    if (this.props.globalState.isGoogleAPI) {
      googleCalendarAPI.signOutGoogle()
    }

    const lan = localStorage.getItem('i18nextLng')
    localStorage.clear()
    this.props.globalState.clearStore()

    this.props.history.push('/')

    localStorage.setItem('i18nextLng', lan)
  }

  handleChangeLanguage = checked => {
    i18next.changeLanguage(checked ? 'vi' : 'en')
    this.setState({
      checked
    })
  }

  render() {
    const {
      i18n,
      globalState
    } = this.props

    const profiles = globalState?.myInfo?.profiles

    const isAllowSettingApp = isSupperAdmin(profiles) || hasPermission(profiles, PER.P_SETTING_UPDATE)
    const isAllowViewReport = isSupperAdmin(profiles) || hasPermission(profiles, PER.P_REPORT_VIEW)
    const isAllowViewUsers = isSupperAdmin(profiles) || hasPermission(profiles, PER.P_USER_VIEW)

    return (
      <div className='container'>
        <ReactNotification />
        <div className='header'>
          <div className='container-header'>
            <div onClick={() => this.props.history.push('/')} className='logoHolder'>
              <img src={logo} alt='logo' />
            </div>
            <ul>
              {/* <li>
                <Link to=''>Schedule a meeting</Link>
              </li> */}
              {/* <li>
                <Dropdown overlay={menu} trigger={['click']}>
                  <Link to=''>
                    Download App <Icon type="caret-down" />
                  </Link>
                </Dropdown>,
              </li> */}
              <li>
                <Link to='/profile'>
                  <AvatarComponent {...globalState.myInfo} />
                </Link>
              </li>
              <li>
                <Link to='' onClick={this.handleLogout}>
                  {i18n.t('menu.signOut')}
                </Link>
              </li>
              <li>
                <Switch
                  className='switch-language'
                  checkedChildren='VI'
                  unCheckedChildren='EN'
                  checked={this.state.checked}
                  onClick={this.handleChangeLanguage}
                />
              </li>
            </ul>
          </div>
        </div>
        <div className='body'>
          <div className='nav-menu'>
            <p className='nav-menu-title'>{i18n.t('menu.personal')}</p>
            <ul>
              <li>
                <NavLink activeClassName='active' to='/profile' exact>
                  {i18n.t('menu.profile')}
                </NavLink>
              </li>
              <li>
                <NavLink
                  activeClassName='active'
                  isActive={(_, location) => {
                    const { pathname } = location
                    if (pathname.includes('conferenceDetail') || pathname === '/') {
                      return true
                    }
                    return false
                  }}
                  to='/'
                  exact
                >
                  {i18n.t('menu.meetings')}
                </NavLink>
              </li>

              <li>
                <NavLink activeClassName='active' to='/recordings' exact>
                  {i18n.t('menu.recordings')}
                </NavLink>
              </li>

              <li>
                <NavLink
                  activeClassName='active'
                  to='/group'
                  isActive={(_, location) => {
                    const { pathname } = location
                    if (pathname.includes('groupDetail') || pathname.includes('group')) {
                      return true
                    }
                    return false
                  }}
                  exact
                >
                  {i18n.t('menu.groups')}
                </NavLink>
              </li>

            </ul>

            {(isAllowSettingApp || isAllowViewReport || isAllowViewUsers) && (
              <p className='nav-menu-title'>{i18n.t('menu.admin')}</p>
            )}

            <ul>
              {isAllowViewUsers && (
                <li>
                  <NavLink
                    activeClassName='active'
                    to='/users'
                    isActive={(_, location) => {
                      const { pathname } = location
                      if (pathname.includes('userInfo') || pathname.includes('users')) {
                        return true
                      }
                      return false
                    }}
                    exact
                  >
                    {i18n.t('menu.users')}
                  </NavLink>
                </li>
              )}

              {isAllowViewReport && (
                <li>
                  <NavLink
                    activeClassName='active'
                    to='/report'
                    isActive={(_, location) => {
                      const { pathname } = location
                      if (pathname.includes('report')) {
                        return true
                      }
                      return false
                    }}
                    exact
                  >
                    {i18n.t('menu.report')}
                  </NavLink>
                </li>
              )}

              {isAllowSettingApp && (
                <li>
                  <NavLink activeClassName='active' to='/settings' exact>
                    {i18n.t('menu.settings')}
                  </NavLink>
                </li>
              )}

            </ul>
          </div>
          <div className='content-body'>{this.props.children}</div>
        </div>
      </div>
    )
  }
}

export default withTranslation()(LayoutDashboard)
