import React from 'react'
import { Modal } from 'antd'

import './DevicesSetting.less'

import SettingBox from './devices-setting-components/SettingBox'

class ModalDevicesSetting extends React.PureComponent {
  state = { visible: false }

  componentDidMount() {
    this.props.handleOpen(this.showModal)
  }

  showModal = () => {
    this.setState({ visible: true })
  }

  handleOk = e => {
    this.setState({ visible: false })
  }

  handleCancel = () => {
    this.setState({ visible: false })
  }

  render() {
    return (
      <Modal
        className='modal-setting'
        title='Speaker , Microphone and Camera'
        visible={this.state.visible}
        centered
        onOk={this.handleOk}
        onCancel={this.handleCancel}
      >
        <div className='wrapper-setting'>
          <SettingBox type='audioOutput' />
          <SettingBox type='audioInput' modalVisible={this.state.visible} />
          <SettingBox type='videoInput' modalVisible={this.state.visible} />
        </div>
      </Modal>
    )
  }
}

export default ModalDevicesSetting
