import React from 'react'
import { Input } from 'antd'
import './InputInvite.less'

import closeIcon from '@misc/images/toolbar/close.svg'

const { TextArea } = Input

class InputInvite extends React.Component {
  state = {
    ctrlDown: false
  }

  handleDownKey = (e) => {
    setTimeout(this.handle, 0)
  }

  handle = () => {
    const re = /^(([^<>()[\]\\.,;:\s@"]+(\.[^<>()[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;
    const data = this.refs['input'].state.value.split(/[\s,\n]+/).map(item => {
      const validate = re.test(String(item).toLowerCase());
      return { value: item, validate }
    })

    this.props.handleAddItem(data)
    this.refs['input'].handleReset()
  }

  handlePressEnter = (e) => {
    if (e.charCode === 13) {
      const email = e.currentTarget.value
      const validate = this.validateEmail(email)
      this.props.handleAddItem([{ value: email, validate }])
      this.refs['input'].handleReset()
      e.preventDefault()
    }
  }

  validateEmail = (email) => {
    var re = /^(([^<>()[\]\\.,;:\s@"]+(\.[^<>()[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;
    return re.test(String(email).toLowerCase());
  }

  render() {
    return (
      <div className='wrapper-input'>
        <div className='wrapper-result'>
          {
            this.props.data.map((item, index) => {
              return (
                <div
                  key={Date.now() + index}
                  className={item.validate ? 'box-email-item validateTrue' : 'box-email-item validateFalse'}
                >
                  <span>{item.value}</span>
                  <img src={closeIcon} alt='icon' onClick={() => this.props.handleDeleteItem(index)} />
                </div>
              )
            })
          }
        </div>
        <TextArea
          ref='input'
          className='input-email'
          autoSize
          onKeyPress={(e) => this.handlePressEnter(e)}
          onPaste={(e) => this.handleDownKey(e)}
        />
      </div>
    )
  }
}

export default InputInvite
