import React from 'react'
import { withTranslation } from 'react-i18next'
import { inject, observer } from 'mobx-react'

import { appConstants, checkPermission, _userStatus } from '@constants'
import { AvatarComponent } from '@components'
import {
  client,
  M_CONFFERENCE_SET_USER_ROLE,
  M_KICK_CONFERENCE_USER,
  M_CHANGE_STATUS_USER,
  M_UPDATE_MUTE_POLICY
} from '@graphql'
import { returnError, addslashes, newNotification } from '@utils'

import dataIcon from '@misc/images/action/data.svg'
import pinUser from '@misc/images/ngocly/pin-icon.svg'
import pinnedIcon from '@misc/images/toolbar/pinned.svg'
import headphone from '@misc/images/ngocly/headphone.svg'
import camera from '@misc/images/ngocly/camera.svg'
import cameraOff from '@misc/images/ngocly/cameraOff.svg'
import micro from '@misc/images/ngocly/micro.svg'
import microOff from '@misc/images/ngocly/microOff.svg'
import logoIcon from '@misc/images/menu/logo.svg'
import moreIcon from '@misc/images/menu/more.svg'
import noSoundBlack from '@misc/images/action/no-sound_black.svg'

import { toJS } from 'mobx'
import { Input, Dropdown, Menu, Button, Tooltip } from 'antd'

const { ROLE, MUTE_POLICY } = appConstants
const { Search } = Input

@inject(({ stores }) => stores)
@observer
class ParticipantsCollapse extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      idTarget: '',
      textSearch: ''
    }
  }

  isConferenceOwner = () => this.props.conferenceState.myRole === ROLE.OWNER

  handleCloseContext = () => {
    this.refs['menu-context'].style.display = 'none'
  }

  getUserDisplayNameFromJID = jid => {
    const userFound = this.props.jitsiStore.participants.find(({ id }) => id === jid)

    if (userFound && userFound.displayName) return userFound.displayName
    return ''
  }

  handleSetRole = roleId => {
    const userDisplayName = this.getUserDisplayNameFromJID(this.state.idTarget)

    if (userDisplayName) {
      const [userId] = userDisplayName.split('|')
      client
        .mutate({
          mutation: M_CONFFERENCE_SET_USER_ROLE,
          variables: { conferenceId: this.props.conferenceState.conference._id, userId, roleId }
        })
        .catch(error =>
          returnError('conferenceSetUserRole', {
            error,
            errorMessage: 'Err! Please try again later!'
          })
        )
    }

    this.handleCloseContext()
  }

  mutationKickConferenceUser = () => {
    const userDisplayName = this.getUserDisplayNameFromJID(this.state.idTarget)

    if (userDisplayName) {
      const [userId] = userDisplayName.split('|')
      client
        .mutate({
          mutation: M_KICK_CONFERENCE_USER,
          variables: { conferenceId: this.props.conferenceState.conference._id, userId }
        })
        .catch(error =>
          returnError('kickConferenceUser', {
            error,
            errorMessage: 'Err! Please try again later!'
          })
        )
    }
  }

  REMOTE_MIC_OFF = (id, hasAudio) => {
    if (this.props.jitsiStore.conference.myUserId() !== id) {
      if (checkPermission(this.props.conferenceState.myPermissions, 'STOP_USER_AUDIO_SHARING') && hasAudio) {
        this.props.jitsiStore.conference.sendCommandOnce('REMOTE_MIC_OFF', { value: id })
      }
    }
  }

  REMOTE_VIDEO_OFF = (id, hasVideo) => {
    if (this.props.jitsiStore.conference.myUserId() !== id) {
      if (checkPermission(this.props.conferenceState.myPermissions, 'STOP_USER_VIDEO_SHARING') && hasVideo) {
        this.props.jitsiStore.conference.sendCommandOnce('REMOTE_VIDEO_OFF', { value: id })
      }
    }
  }

  REMOTE_KICK_USER = id => {
    if (this.props.jitsiStore.conference.myUserId() !== id) {
      if (checkPermission(this.props.conferenceState.myPermissions, 'KICK_USER')) {
        this.props.jitsiStore.conference.sendCommandOnce('REMOTE_KICK_USER', { value: id })
      }
    }
  }

  componentDidMount() {
    document.addEventListener('mousedown', this.handleCloseContextMenu)
  }

  handleCloseContextMenu = event => {
    if (this.refs['menu-context'] && !this.refs['menu-context'].contains(event.target)) {
      this.handleCloseContext()
    }
  }

  handleOpenContextMenu = e => {
    const { conferenceState } = this.props

    const tempX = 380 - (window.innerWidth - e.pageX)
    const menuX = tempX > 200 ? tempX - 200 : tempX
    const menuY = e.pageY

    this.refs['menu-context'].style.display = 'block'
    this.refs['menu-context'].style.transform = `translate(${menuX}px, ${menuY}px)`

    if (checkPermission(conferenceState.myPermissions, 'AUTHORIZE')) {
      if (tempX < 80) {
        this.refs['menu-child'].style.left = '100%'
        this.refs['menu-child'].style.transform = 'translate(0%, 0%)'
      } else {
        this.refs['menu-child'].style.left = '0%'
        this.refs['menu-child'].style.transform = 'translate(-100%, 0%)'
      }
    }
  }

  checkShowPermission = roleId => {
    if (this.state.idTarget) {
      const userDisplayName = this.getUserDisplayNameFromJID(this.state.idTarget)
      if (userDisplayName) {
        const [userId] = userDisplayName.split('|')

        if (userId) {
          const userProfile = this.props.conferenceState.conferenceUsers.find(({ user }) => user && user._id === userId)

          if (userProfile) return userProfile.role._id === roleId
        }
      }
    }

    return false
  }

  getPermission = (userId, roleId) => {
    let label = []

    if (roleId === ROLE.OWNER) label.push('Host')
    if (roleId === ROLE.MODERATOR) label.push('Presenter')
    if (roleId === ROLE.PARTICIPANT) label.push('Participant')

    if (this.props.conferenceState.myUserId === userId) label.push('me')

    if (label.length) {
      if (label.length > 1) return `   (${label.join(', ')})`
      return `   (${label})`
    }

    return null
  }

  mutateChangeStatus = (userId, status) => {
    client
      .mutate({
        mutation: M_CHANGE_STATUS_USER,
        variables: {
          conferenceId: this.props.conferenceState.conference._id,
          userId,
          status
        }
      })
      .then(({ data }) => console.log(data))
      .catch(err => console.log(err))
  }

  filterFunc = (participant, textSearch) => {
    if (!participant.info) return false

    const {
      userFullName,
      info: { username, email, phoneNumber }
    } = participant

    const regex = new RegExp(addslashes(textSearch), 'i')

    return regex.test(userFullName) || regex.test(username) || regex.test(email) || regex.test(phoneNumber)
  }

  hanldeSearchParticipant = ({ target: { value } }) => {
    this.setState({ textSearch: value })
  }

  handleChangeMutePolicy = mutePolicy => {
    client
      .mutate({
        mutation: M_UPDATE_MUTE_POLICY,
        variables: {
          conferenceId: this.props.conferenceState.conference._id,
          mutePolicy
        }
      })
      .then(({ data: { updateMutePolicy } }) => {
        if (updateMutePolicy) {
          const { setState, conference } = this.props.conferenceState
          return setState({
            conference: { ...conference, mutePolicy: mutePolicy }
          })
        }
        return newNotification('Can not change mute policy at the moment, please try again later!', 'danger')
      })
      .catch(error =>
        returnError('updateMutePolicy', {
          error,
          errorMessage: 'Can not change mute policy at the moment, please try again later!'
        })
      )
  }

  handleDropdownSelect = ({ key }) => {
    switch (key) {
      case MUTE_POLICY.SOFT: {
        return this.handleChangeMutePolicy(MUTE_POLICY.SOFT)
      }

      case MUTE_POLICY.HARD: {
        return this.handleChangeMutePolicy(MUTE_POLICY.HARD)
      }

      case MUTE_POLICY.PUSH_TO_TALK: {
        return this.handleChangeMutePolicy(MUTE_POLICY.PUSH_TO_TALK)
      }

      case MUTE_POLICY.PRIORITY: {
        return this.handleChangeMutePolicy(MUTE_POLICY.PRIORITY)
      }

      default: {
      }
    }
  }

  render() {
    const { conferenceState, jitsiStore } = this.props
    const { textSearch } = this.state

    const selectedTabs = [
      conferenceState.isShowPoll,
      conferenceState.isShowPollManagement,
      conferenceState.isShowParticipants,
      conferenceState.isShowMessages,
      conferenceState.isShowAttendancesManagement,
      conferenceState.isShowQnAManagement,
      conferenceState.isShowQnA
    ].filter(e => e).length

    const userWaiting = conferenceState.conferenceUsers.filter(({ status }) => status === _userStatus.WAITING)

    const participants = toJS(jitsiStore.participants)
      .map(participant => {
        if (participant?.user?._displayName) {
          const [userId, userFullName] = participant.user._displayName.split('|')
          const userFound = conferenceState.conferenceUsers.find(({ user }) => user._id === userId)
          participant.info = {
            ...toJS(userFound.user),
            role: userFound.role
          }
          participant.userFullName = userFullName
        }
        return participant
      })
      .filter(participant => (textSearch ? this.filterFunc(participant, textSearch) : !!participant.info))

    return (
      <div className='participants-container' style={{ height: `calc(100vh - ${selectedTabs * 20 + 1}px)` }}>
        {conferenceState.conference.createdBy._id === conferenceState.myUserId && userWaiting.length !== 0 && (
          <div className='participants-waiting'>
            <div className='title'>{userWaiting.length} person is waiting</div>
            {userWaiting.map(({ user }) => (
              <div className='participants-info' key={user._id}>
                <AvatarComponent {...user} size='small' />
                <img className='participants-headphone' src={headphone} alt='icon' />
                <div className='participants-name'>
                  {user.firstname} {user.lastname}
                </div>
                <div className='group-btn'>
                  <button type='button' onClick={() => this.mutateChangeStatus(user._id, _userStatus.ONLINE)}>
                    Admit
                  </button>
                  <button onClick={() => this.mutateChangeStatus(user._id, _userStatus.OFFLINE)} type='button'>
                    Remove
                  </button>
                </div>
              </div>
            ))}
          </div>
        )}

        <div className='wrapper-control-btn'>
          <Search
            className='search-participant'
            placeholder='Tìm theo Tên/Email/SĐT'
            onChange={this.hanldeSearchParticipant}
          />

          {this.isConferenceOwner() && (
            <Dropdown
              overlay={
                <Menu
                  selectedKeys={[this.props.conferenceState.conference.mutePolicy]}
                  className='mute-policy-menu'
                  onClick={this.handleDropdownSelect}
                >
                  <Menu.Item key={MUTE_POLICY.HARD}>
                    <Tooltip placement='left' title='Chỉ chủ phòng mới được phép mở micphone người dùng.'>
                      <img src={noSoundBlack} alt='icon' />
                    </Tooltip>
                    <span>Hard Mute</span>
                  </Menu.Item>

                  <Menu.Item key={MUTE_POLICY.SOFT}>
                    <Tooltip placement='left' title='Người dùng được phép mở tắt micphone.'>
                      <img src={noSoundBlack} alt='icon' />
                    </Tooltip>
                    <span>Soft Mute</span>
                  </Menu.Item>

                  <Menu.Item key={MUTE_POLICY.PUSH_TO_TALK}>
                    <Tooltip placement='left' title='Người dùng phải giữ đè phím Z để nói.'>
                      <img src={noSoundBlack} alt='icon' />
                    </Tooltip>
                    <span>Push To Talk</span>
                  </Menu.Item>

                  <Menu.Item key={MUTE_POLICY.PRIORITY}>
                    <Tooltip placement='left' title='Chỉ Host, Presenter và Participants được Pin mới có thể nói.'>
                      <img src={noSoundBlack} alt='icon' />
                    </Tooltip>
                    <span>Discussion Mode</span>
                  </Menu.Item>
                </Menu>
              }
            >
              <Button shape='circle' className='btn-mute-policy'>
                <img src={noSoundBlack} alt='icon' />
              </Button>
            </Dropdown>
          )}
        </div>

        {!textSearch && (
          <div className='participants-info' onContextMenu={e => e.preventDefault()}>
            <AvatarComponent {...conferenceState.myInfo} size='small' />
            <img className='participants-headphone' src={headphone} alt='icon' />
            <div className='participants-name'>
              {conferenceState.myInfo.firstname} {conferenceState.myInfo.lastname}
              <span>{this.getPermission(conferenceState.myUserId, conferenceState.myRole)}</span>
            </div>
            <div className='participants-group-btn'>
              <button type='button' disabled>
                <img src={jitsiStore.isCamera ? camera : cameraOff} alt='icon' />
              </button>
              <button type='button' disabled>
                <img src={jitsiStore.isAudio ? micro : microOff} alt='icon' />
              </button>
            </div>
          </div>
        )}

        {textSearch && !participants.length && (
          <div className='wrapper-no-data'>
            <img src={dataIcon} alt='icon_no_data' />
            <span>No Participant</span>
          </div>
        )}

        {participants.map(({ id, userFullName, info }) => {
          const hasAudio = this.props.doesParticipantHasAudio(id)
          const hasVideo = this.props.doesParticipantHasVideo(id)

          return (
            <div
              key={id}
              className='participants-info'
              data-user={id}
              onContextMenu={e => {
                e.preventDefault()
                if (
                  jitsiStore.conference.myUserId() !== id &&
                  (info.role._id !== ROLE.OWNER || checkPermission(conferenceState.myPermissions, 'KICK_USER'))
                ) {
                  this.setState({ idTarget: id })
                  this.handleOpenContextMenu(e)
                }
              }}
            >
              <AvatarComponent {...info} size='small' />
              <img className='participants-headphone' src={headphone} alt='icon' />
              <div className='participants-name'>
                {userFullName || 'Loading...'}
                <span>{this.getPermission(id, info.role._id)}</span>
              </div>

              <div className='participants-group-btn'>
                {checkPermission(conferenceState.myPermissions, 'PIN_MAIN_SPEAKER') ? (
                  <button
                    className={conferenceState.conference.pinnedUser === id ? 'btn-pin pin-active' : 'btn-pin'}
                    onClick={() => {
                      if (checkPermission(conferenceState.myPermissions, 'PIN_MAIN_SPEAKER')) {
                        this.props.mutateSetPinnedUser(conferenceState.conference.pinnedUser === id ? '' : id)
                      }
                    }}
                  >
                    <img src={conferenceState.conference.pinnedUser === id ? pinnedIcon : pinUser} alt='icon' />
                  </button>
                ) : (
                  <React.Fragment />
                )}

                <button
                  type='button'
                  disabled={info.role._id === ROLE.OWNER}
                  onClick={() => this.REMOTE_VIDEO_OFF(id, hasVideo)}
                >
                  <img src={hasVideo ? camera : cameraOff} alt='icon' />
                </button>

                <button
                  type='button'
                  disabled={info.role._id === ROLE.OWNER}
                  onClick={() => this.REMOTE_MIC_OFF(id, hasAudio)}
                >
                  <img src={hasAudio ? micro : microOff} alt='icon' />
                </button>
              </div>
            </div>
          )
        })}

        <div className='menu-context' ref='menu-context'>
          <ul className='menu-parent'>
            {checkPermission(conferenceState.myPermissions, 'AUTHORIZE') ? (
              <li className='item-more border-item'>
                <div className='item-more-text border-item'>
                  <span>Change Role To</span>
                  <img src={moreIcon} alt='icon' />
                </div>
                <ul className='menu-child' ref='menu-child'>
                  <li
                    className={this.checkShowPermission('MODERATOR') ? 'permission-active' : null}
                    onClick={() => {
                      if (!this.checkShowPermission('MODERATOR')) {
                        this.handleSetRole('MODERATOR')
                      }
                    }}
                  >
                    {this.checkShowPermission('MODERATOR') ? <img src={logoIcon} alt='icon' /> : <React.Fragment />}
                    <span>Presenter</span>
                  </li>
                  <li
                    className={this.checkShowPermission('PARTICIPANT') ? 'permission-active' : null}
                    onClick={() => {
                      if (!this.checkShowPermission('PARTICIPANT')) {
                        this.handleSetRole('PARTICIPANT')
                      }
                    }}
                  >
                    {this.checkShowPermission('PARTICIPANT') ? <img src={logoIcon} alt='icon' /> : <React.Fragment />}
                    <span>Participant</span>
                  </li>
                </ul>
              </li>
            ) : (
              <React.Fragment />
            )}

            {checkPermission(conferenceState.myPermissions, 'STOP_USER_AUDIO_SHARING') ? (
              <li
                className={this.props.doesParticipantHasAudio(this.state.idTarget) ? '' : 'disable'}
                onClick={() => {
                  if (
                    checkPermission(conferenceState.myPermissions, 'STOP_USER_AUDIO_SHARING') &&
                    this.props.doesParticipantHasAudio(this.state.idTarget)
                  ) {
                    this.REMOTE_MIC_OFF(this.state.idTarget, true)
                    this.handleCloseContext()
                  }
                }}
              >
                <span className='border-item'>Mute</span>
              </li>
            ) : (
              <React.Fragment />
            )}
            {checkPermission(conferenceState.myPermissions, 'STOP_USER_VIDEO_SHARING') ? (
              <li
                className={this.props.doesParticipantHasVideo(this.state.idTarget) ? '' : 'disable'}
                onClick={() => {
                  if (
                    checkPermission(conferenceState.myPermissions, 'STOP_USER_VIDEO_SHARING') &&
                    this.props.doesParticipantHasVideo(this.state.idTarget)
                  ) {
                    this.REMOTE_VIDEO_OFF(this.state.idTarget, true)
                    this.handleCloseContext()
                  }
                }}
              >
                <span className='border-item'>Stop Video</span>
              </li>
            ) : (
              <React.Fragment />
            )}

            {checkPermission(conferenceState.myPermissions, 'CHAT') ? (
              <li
                onClick={() => {
                  if (this.state.idTarget) {
                    const userFound = jitsiStore.participants.find(({ id }) => id === this.state.idTarget)

                    if (userFound && userFound.displayName) {
                      const [profileFound] = conferenceState.conferenceUsers.filter(
                        ({ user: { _id } }) => _id === userFound.displayName.split('|')[0]
                      )

                      conferenceState.setState({
                        idChatUser: userFound.displayName.split('|')[0],
                        isShowBoxChat: true,
                        infoChatUser: {
                          _id: userFound.displayName.split('|')[0],
                          name: userFound.displayName.split('|')[1],
                          imageUrl: profileFound ? profileFound.user.imageUrl : null
                        }
                      })
                    }

                    this.handleCloseContext()
                  }
                }}
              >
                <span>Chat</span>
              </li>
            ) : (
              <React.Fragment />
            )}

            {checkPermission(conferenceState.myPermissions, 'KICK_USER') && (
              <li
                onClick={() => {
                  this.REMOTE_KICK_USER(this.state.idTarget)
                  this.handleCloseContext()
                }}
              >
                <span>Kick user</span>
              </li>
            )}
          </ul>
        </div>
      </div>
    )
  }
}

export default withTranslation()(ParticipantsCollapse)
