import React from 'react'
import { Tooltip, Button } from 'antd'
import { inject, observer } from 'mobx-react'

import CameraOn from './components/AnimatedCameraOnSVG'
import CameraOff from './components/AnimatedCameraOffSVG'

@inject(({ stores }) => stores)
@observer
class ButtonCamera extends React.PureComponent {
  handleToggleShareCamera = () => {
    if (this.props.jitsiStore.isCamera) {
      this.props.offCamera()
    } else {
      this.props.onCamera()
    }
  }

  render() {
    const { jitsiStore } = this.props

    return (
      <div className='action-item action-item-micro'>
        <Tooltip title='Camera'>
          <Button
            type='primary'
            className={`${jitsiStore.isCamera ? '' : 'btn-off'} ${
              jitsiStore.isShareCameraInProcess ? 'disabled-btn' : ''
            }`}
            onClick={this.handleToggleShareCamera}
          >
            {jitsiStore.isCamera ? <CameraOn /> : <CameraOff />}
          </Button>
        </Tooltip>
      </div>
    )
  }
}

export default ButtonCamera
