import { observable, action } from 'mobx'
import { appConstants } from '@constants'

export class GlobalState {
  @observable
  isAuth = !!window.localStorage.getItem(appConstants.LSKey.userTokenKey)

  @observable
  isMobile = false

  @observable
  myInfo = null

  @observable
  myPermissions = null

  @observable
  redirectTo = null

  @observable
  queryMyInfo = null

  @observable
  isGoogleAPI = false

  @action
  setisGoogleAPI = value => {
    this.isGoogleAPI = value
  }

  @action
  auth = token => {
    window.localStorage.setItem(appConstants.LSKey.userTokenKey, token)
    this.isAuth = true
  }

  @action
  setIsMobile = isMobile => {
    this.isMobile = isMobile
  }

  @action
  clearStore = () => {
    this.isAuth = false
    this.redirectTo = null
  }

  @action
  setMyInfo = myInfo => {
    this.myInfo = myInfo
    this.myPermissions = myInfo.managementRole
  }


  @action
  setQueryMyInfo = func => {
    this.queryMyInfo = func
  }

  @action
  setRedirectTo = url => {
    this.redirectTo = url
  }
}
