import React from 'react'
import { Form, Input, Radio } from 'antd'
import './QuestionPoll.less'

const { TextArea } = Input

class HorizontalLoginForm extends React.PureComponent {
  state = {
    valueRadio: 'SINGLE',
    order: 3,
    answers: [
      {
        key: 'ANS-1',
        value: ''
      },
      {
        key: 'ANS-2',
        value: ''
      },
      {
        key: 'ANS-3',
        value: ''
      }
    ]
  }

  handleAddAnswer = () => {
    this.setState(_preState => ({
      order: _preState.order + 1,
      answers: [..._preState.answers, { key: `ANS-${_preState.order + 1}`, value: '' }]
    }))
  }

  handleDeleteAnswer = (index) => {
    this.setState(_preState => ({
      answers: [..._preState.answers.slice(0, index), ..._preState.answers.slice(index + 1, _preState.answers.length)]
    }))
  }

  handleChangeType = e => {
    this.setState({
      valueRadio: e.target.value,
    })
  }

  render() {
    const {
      props: {
        handleDeleteQuesion,
        index,
        lenQuestion,
        form: { getFieldDecorator }
      }
    } = this

    return (
      <div className='wrapper-question-poll'>
        <div className='header-question-poll'>
          <p>{`Question: ${index + 1}`}</p>
          {
            lenQuestion > 1 ? (
              <button
                className='btn-delete-question'
                onClick={() => handleDeleteQuesion(index)}
              >
                Delete
              </button>
            ) : null
          }
        </div>
        <div className='wrapper-question-item'>
          <Form.Item>
            {getFieldDecorator(`question`, {
              rules: [{ required: true }],
            })(<TextArea rows={4} />)
            }
          </Form.Item>
          <Form.Item>
            {getFieldDecorator('type', {
              initialValue: 'SINGLE'
            })(
              <Radio.Group>
                <Radio value='SINGLE'>Single Choice</Radio>
                <Radio value='MULTIPLE'>Multiphe Choice</Radio>
              </Radio.Group>
            )
            }
          </Form.Item>
          <div className='wrapper-answer-poll'>
            {
              this.state.answers.map((item, index) => {
                return (
                  <div className='answer-item' key={item.key}>
                    <p className='order-answer'>{String.fromCharCode(index + 65)}</p>
                    <Form.Item>
                      {getFieldDecorator(item.key, {
                        rules: [{ required: true }],
                      })(<Input placeholder={`Answer ${String.fromCharCode(index + 65)}`} />)
                      }
                    </Form.Item>
                    {
                      this.state.answers.length > 2 ? (
                        <button
                          className='btn-delete-answer'
                          onClick={() => this.handleDeleteAnswer(index)}
                        >
                          Delete
                        </button>
                      ) : null
                    }
                  </div>
                )
              })
            }
          </div>
          {
            this.state.answers.length < 6 ? (
              <button className='btn-add-answer' onClick={this.handleAddAnswer}>Add answer</button>
            ) : null
          }
        </div>
      </div>
    )
  }
}

const QuestionPoll = Form.create({ name: 'horizontal_login' })(HorizontalLoginForm)

export default QuestionPoll
