import React, { Component } from 'react'
import { inject, observer } from 'mobx-react'
import { Table, Icon } from 'antd'

@inject(({ stores }) => stores)
@observer
class CurrentAttendanceView extends Component {
  render() {
    const {
      props: { attendanceInfo, conferenceUsers }
    } = this

    const rowsData = conferenceUsers.map(({ user }, index) => ({
      key: index,
      fullName: user.firstname + ' ' + user.lastname,
      userId: user._id
    }))

    const columns = [
      {
        title: 'Họ và Tên',
        dataIndex: 'fullName',
        key: 'fullName'
      },
      {
        title: 'Điểm danh',
        dataIndex: 'userId',
        className: 'attended',
        key: 'userId',
        width: 100,
        render: userId => (
          <span>
            {attendanceInfo.userAttendances
              .filter(e => e.isAttended)
              .map(e => e.user._id)
              .includes(userId) ? (
              <Icon type='check' />
            ) : (
              <></>
            )}
          </span>
        )
      }
    ]

    return <Table size={'small'} pagination={false} columns={columns} dataSource={rowsData} rowKey='userId' />
  }
}
export default CurrentAttendanceView
