import React from 'react'
import { withTranslation } from 'react-i18next'
import { inject, observer } from 'mobx-react'
import './ListMenu.less'

import { checkPermission } from '@constants'

import more from '@misc/images/arrow-right.svg'

@inject(({ stores }) => stores)
@observer
class ListContent extends React.Component {
  render() {
    const {
      props: { data, handleChoiceMenu, i18n, conferenceState }
    } = this

    return data.map((item, i) => {
      return (
        <div className='list' key={i}>
          <div className='list__head'>
            <p>{i18n.t(item.name)}</p>
          </div>
          <div className='list__body'>
            {item.list.map((item1, i1) => {
              return checkPermission(conferenceState.myPermissions, item1.code) ? (
                <div
                  className='list__item'
                  onClick={() => handleChoiceMenu(item1.link)}
                  key={`${i}${i1}`}
                >
                  <div className='item__img'>
                    <img src={item1.icon} alt='icon' />
                  </div>
                  <div className='item__text'>
                    <span>{i18n.t(item1.text)}</span>
                    {item1.isMore ? <img src={more} alt='icon' /> : <></>}
                  </div>
                </div>
              ) : null
            })}
          </div>
        </div>
      )
    })
  }
}

export default withTranslation()(ListContent)
