import React from 'react'
import { withTranslation } from 'react-i18next'
import { Form, Input, Select, TimePicker, DatePicker, Button, Checkbox, Radio, Col, Row } from 'antd'
import moment from 'moment'
import './CreateWebinar.less'


import { client, M_CREATE_WEBINAR, Q_WEBINAR_TEMPLATE } from '@graphql'
import { returnError, newNotification } from '@utils'

const { Option } = Select

const options = {
  QnA: 'A',
  practiceSession: 'B',
  authenticatedOnly: 'C',
  automaticallyRecord: 'D'
}

class FormCreateWebinar extends React.Component {
  state = {
    alwaysAvailable: false,
    recievedChecboxValue: [],
    hostVideo: false,
    panelistsVideo: false
  }

  convertTime = (date, time) => {
    const newDate = new Date(
      date._d.getFullYear(),
      date._d.getMonth(),
      date._d.getDate(),
      time._d.getHours(),
      time._d.getMinutes(),
      time._d.getSeconds()
    )
    return newDate.getTime()
  }

  handleSelectTemplate = e => {
    client
      .query({
        query: Q_WEBINAR_TEMPLATE,
        fetchPolicy: 'network-only',
        variables: {
          webinarTeamplateId: e
        }
      })
      .then(({ data: { webinarTemplate } }) => {
        const { name,
          hostVideo,
          panelistsVideo,
          QnA,
          practiceSession,
          authenticatedOnly,
          automaticallyRecord } = webinarTemplate[0]

        this.setState((prevState => {
          const temp = [
            options[QnA ? 'QnA' : null],
            options[practiceSession ? 'practiceSession' : null],
            options[authenticatedOnly ? 'authenticatedOnly' : null],
            options[automaticallyRecord ? 'automaticallyRecord' : null]
          ].filter(e => e !== undefined)

          return { recievedChecboxValue: temp, hostVideo, panelistsVideo }
        }), () => {
          this.props.form.setFieldsValue({
            name
          })
        })
      })
      .catch(error =>
        returnError(
          'Get webinar templates',
          {
            error,
            errorMessage: 'Cannot get webinar template list, try again later!'
          },
          { container: 'top-center' }
        )
      )
  }

  handleSubmit = e => {
    e.preventDefault()
    if (this.state.alwaysAvailable)
      this.props.form.setFieldsValue({ date: null, time: null })
    this.props.form.validateFields((err, values) => {
      const startAt =
        values.date && values.time
          ? this.convertTime(values.date, values.time)
          : null

      const QnA = values['default-option'].indexOf('A') !== -1
      const practiceSession = values['default-option'].indexOf('B') !== -1
      const authenticatedOnly = values['default-option'].indexOf('C') !== -1
      const automaticallyRecord = values['default-option'].indexOf('D') !== -1
      //MUTATION CREATE CONFERENCE
      client
        .mutate({
          mutation: M_CREATE_WEBINAR,
          variables: {
            newWebinar: {
              name: values.name,
              password: values.password || '',
              startAt,
              defaultParticipantRole: values.role,
              hostVideo: values['radio-host'] === 'A',
              panelistsVideo: values['radio-panelists'] === 'A',
              QnA,
              practiceSession,
              authenticatedOnly,
              automaticallyRecord
            }
          }
        })
        .then(({ data: { createWebinar } }) => {
          this.props.form.resetFields()
          if (createWebinar) {
            this.props.refetch()
            this.props.backToDefault()
            newNotification('Create Webinar Success', 'success', { container: 'top-right' })
          }
        })
        .catch(error =>
          returnError(
            'Get Link',
            {
              error,
              errorMessage: 'Cannot create conference, try again later!'
            },
            { container: 'top-center' }
          )
        )
    })
  }

  componentDidMount() {
    if (this.props.selectedTemplate) {
      this.handleSelectTemplate(this.props.selectedTemplate._id)
      this.props.form.setFieldsValue({
        template: this.props.selectedTemplate._id
      })
    }
  }

  render() {
    const {
      props: {
        form: { getFieldDecorator },
        webinarTemplates,
        i18n
      }
    } = this

    return (
      <Form
        onSubmit={this.handleSubmit}
        className='form-create-webinar'
        ref='form'
        style={{ width: '60%' }}
      >
        <h3>{i18n.t('createWebinar')}</h3>
        <Form.Item label={i18n.t('webinarName')}>
          {getFieldDecorator('name', {
            rules: [
              {
                required: true,
                message: 'Topic!'
              }
            ]
          })(<Input style={{ width: '100%' }} placeholder='The Webinar 8' />)}
        </Form.Item>
        <Form.Item label={i18n.t('default')}>
          <Checkbox onChange={(e) => this.setState({ alwaysAvailable: e.target.checked })}>{i18n.t('alwaysAvailable')}</Checkbox>
        </Form.Item>
        <Form.Item label={i18n.t('template')} style={{ flex: 1, marginRight: '0.5rem' }}>
          {getFieldDecorator('template')(
            <Select
              showSearch
              placeholder="Select a template"
              onChange={this.handleSelectTemplate}
            >
              {
                webinarTemplates.map(e => (
                  <Select.Option key={e._id} value={e._id}>
                    {e.name}
                  </Select.Option>
                ))
              }
            </Select>
          )}
        </Form.Item>
        <Form.Item label={i18n.t('startDate')} style={{ flex: 1, marginRight: '0.5rem' }}>
          {getFieldDecorator('date')(<DatePicker disabled={this.state.alwaysAvailable} />)}
        </Form.Item>
        <Form.Item label={i18n.t('startTime')} style={{ flex: 1, marginLeft: '0.5rem' }}>
          {getFieldDecorator('time')(
            <TimePicker
              disabled={this.state.alwaysAvailable}
              defaultOpenValue={moment('00:00:00', 'HH:mm:ss')}
              style={{ width: '100%' }}
            />
          )}
        </Form.Item>
        <Form.Item label={i18n.t('password')}>
          {getFieldDecorator('password')(
            <Input type='password' style={{ width: '100%' }} placeholder='12345678' />
          )}
