            <Form.Item label={i18n.t('panelist')} style={{ padding: '0px 0px 0px 5px' }}>
              {getFieldDecorator('radio-panelists', {
                initialValue: this.state.panelistsVideo ? 'A' : 'B'
              })(
                <Radio.Group>
                  <Radio value='A'>{i18n.t('on')}</Radio>
                  <Radio value='B'>{i18n.t('off')}</Radio>
                </Radio.Group>,
              )}
            </Form.Item>
          </Col>
        </Row>
        <Form.Item label={i18n.t('webinarOptions')}>
          {getFieldDecorator('default-option', {
