              <Checkbox value="A">{i18n.t('Q&A')}</Checkbox>
              <Checkbox value="B">{i18n.t('enablePracticeSession')}</Checkbox>
              <Checkbox value="C">{i18n.t('onlyAuthenticated')}</Checkbox>
              <Checkbox value="D">{i18n.t('recordAutomatically')}</Checkbox>
            </Checkbox.Group>,
          )}
        </Form.Item>
        <Form.Item label={i18n.t('userRole')}>
          {getFieldDecorator('role', { initialValue: 'PARTICIPANT' })(
            <Select style={{ width: '100%' }}>
              <Option value='MODERATOR'>{i18n.t('owner')}</Option>
              <Option value='PARTICIPANT'>{i18n.t('participants')}</Option>
            </Select>
          )}
        </Form.Item>
        <div
          style={{ display: 'flex', flexDirection: 'row' }}
        >
          <Form.Item style={{ flex: 1, marginRight: '0.5rem', display: 'flex', justifyContent: 'flex-end' }}>

            {/* <Button
              onClick={backToDefault}
              className='login-form-button'
              style={{ width: '10rem' }}
            >
              Huỷ
            </Button> */}
          </Form.Item>
          <Form.Item style={{ flex: 1, marginLeft: '0.5rem' }}>
            <Button
              type='primary'
              style={{ width: '10rem', height: '38px', borderRadius: '8px', fontWeight: '600px' }}
              htmlType='submit'
              className='login-form-button'
            >
              {i18n.t('save')}
            </Button>
          </Form.Item>
        </div>

      </Form>
    )
  }
}

