import React from 'react'
import { withTranslation } from 'react-i18next'
import { withRouter } from 'react-router-dom'
import { inject, observer } from 'mobx-react'
import { client, M_CREATE_VOTE } from '@graphql'
import { newNotification } from '@utils'

import './TrueFalseQuestion.less'

@withRouter
@inject(({ stores }) => stores)
@observer
class TrueFalseQuestion extends React.Component {
  // Create Vote
  handleCreateVote = () => {
    const dataAnswers = [
      {
        key: 'A',
        value: 'Yes'
      },
      {
        key: 'B',
        value: 'No'
      }
    ]
    const dataQuestion = this.refs.question.value
    // MUTATION CREATE VOTE
    client
      .mutate({
        mutation: M_CREATE_VOTE,
        variables: {
          newPollInput: {
            conferenceId: this.props.conferenceState.conference._id,
            question: dataQuestion,
            answers: dataAnswers,
            type: 'Yes/No'
          }
        }
      })
      .catch(error => {
        return newNotification('Please try again later!', 'danger')
      })
    this.props.handleBack('menu')
  }

  render() {
    const {
      handleCreateVote,
      props: { handleBack, i18n }
    } = this

    return (
      <>
        <div className='truefalse-question'>
          <div className='truefalse-question-box'>
            <textarea placeholder={i18n.t('makeQuestion')} ref='question' />
            <div className='truefalse-question__answer'>
              <div className='answer-item'>
                <p>1</p>
                <span>{i18n.t('yes')}</span>
              </div>
              <div className='answer-item'>
                <p>2</p>
                <span>{i18n.t('no')}</span>
              </div>
            </div>
          </div>
        </div>
        <div className='action-box'>
          <button
            type='button'
            className='btn-cancel'
            onClick={() => handleBack('vote')}
          >
            {i18n.t('cancel')}
          </button>
          <button
            type='button'
            className='btn-start'
            onClick={handleCreateVote}
          >
            {i18n.t('start')}
          </button>
        </div>
      </>
    )
  }
}

export default withTranslation()(TrueFalseQuestion)
