import React from 'react'
import { inject, observer } from 'mobx-react'
import { Modal } from 'antd'
import { client, M_STOP_RECORD } from '@graphql'
import { newNotification, parseError } from '@utils'
import { withTranslation } from 'react-i18next'
import './ModalConfirmStopRecord.less'
@inject(({ stores }) => stores)
@observer
class ModalConfirmStopRecord extends React.PureComponent {
  state = {
    visible: false
  }

  mutationStopRecord = () => {
    client
      .mutate({
        mutation: M_STOP_RECORD,
        variables: {
          conferenceId: this.props.conferenceState.conference._id
        }
      })
      .then(res => {
        if (res?.data?.stopRecord) {
          return newNotification('Đã tắt ghi hình!', 'success')
        }
        throw new Error('Có lỗi xảy ra!')
      })
      .catch(err => {
        return newNotification(this.props.i18n.t(parseError(err)), 'danger')
      })
  }

  componentDidMount() {
    this.props.showDialog(this.showModal)
  }

  showModal = () => {
    this.setState({
      visible: true
    })
  }

  handleOk = e => {
    this.mutationStopRecord()
    this.setState({
      visible: false
    })
  }

  handleCancel = e => {
    this.setState({
      visible: false
    })
  }

  render() {
    return (
      <Modal
        className='modal-stop-record'
        title='Stop Recording'
        visible={this.state.visible}
        onOk={this.handleOk}
        onCancel={this.handleCancel}
        centered
      >
        <p>Do you want to stop the recording?</p>
        <p>If you stop the recording and then restart at a later stage, a separate recording file will be created.</p>
        <p>To pause the recording instead, click the Pause recording button in the Recorder panel.</p>
      </Modal>
    )
  }
}

export default withTranslation()(ModalConfirmStopRecord)
