import React, { Component } from 'react'
import { withTranslation } from 'react-i18next'
import { Row, Col, Button, Icon, Tabs } from 'antd'
import moment from 'moment'

import { client, Q_WEBINAR_POLLS } from '@graphql'
import { returnError, newNotification } from '@utils'

import ModalCreateWebinar from './components/ModalCreateWebinar'
import PollComponent from './components/PollComponent'
// import QAComponent from './components/QAComponent'
import InvitationsComponent from './components/InvitationsComponent'
// import EmailSettingComponent from './components/EmailSettingComponent'

const { TabPane } = Tabs


class DetailWebinar extends Component {
  state = {
    visible: false,
    users: [],
    webinarPolls: []
  }

  queryWebinarPoll = () => {
    client
      .query({
        query: Q_WEBINAR_POLLS,
        fetchPolicy: 'network-only',
        variables: {
          webinarId: this.props.webinar._id
        }
      })
      .then(({ data: { webinarPolls } }) => {
        this.setState({ webinarPolls })
        newNotification('Create webinar poll success', 'success', { container: 'top-right' })
      })
      .catch(error =>
        returnError(
          'Get webinar templates',
          {
            error,
            errorMessage: 'Cannot create webinar poll, try again later!'
          },
          { container: 'top-right' }
        )
      )
  }

  showModal = () => {
    this.setState({
      visible: true,
      listInvite: []
    })
  }

  handleCancel = e => {
    this.setState({
      visible: false,
      listInvite: []
    })
  }

  handleChangeTab = (key) => {
    if (key === '2') {
      this.queryWebinarPoll()
    }
  }

  render() {
    const {
      props: {
        webinar,
        i18n
      }
    } = this

    return (
      <div className='detail-template'>
        <Row>
          <Col span={6}></Col>
          <Col span={18}>
            <Button
              style={{ borderRadius: '8px', float: 'right' }}
              type='primary'
            >
              {i18n.t('startWebinar')}
            </Button>
          </Col>
        </Row>
        <Row>
          <Col span={6}>{i18n.t('topic')}</Col>
          <Col span={18} className='highlight'>{webinar.name}</Col>
        </Row>

        <Row>
          <Col span={6}>{i18n.t('time')}</Col>
          <Col span={18}>
            {webinar.startAt ? moment(webinar.startAt + webinar.startDate).format('MMM Do YYYY, hh:mm a') : 'Always Available'}
          </Col>
        </Row>

        <Row>
          <Col span={6}>Webinar ID</Col>
          <Col span={18}>{webinar._id}</Col>
        </Row>

        <Row>
          <Col span={6}>{i18n.t('createBy')}</Col>
          <Col span={18}>
            {
              webinar.createdBy
                ? `${webinar.createdBy.firstname} ${webinar.createdBy.lastname}`
                : ''
            }
          </Col>
        </Row>

        <Row>
          <Col span={6}>{i18n.t('capacity')}</Col>
          <Col span={18}>Webinar 50</Col>
        </Row>

        <Row>
          <Col span={6}>{i18n.t('defaultRole')}</Col>
          <Col span={18}>{webinar.defaultParticipantRole}</Col>
        </Row>

        <Row style={{ display: 'flex', alignItems: 'flex-start', border: '0' }}>
          <Col span={6}>Video:</Col>
          <Col span={18}>
            <Row style={{ border: '0', padding: '5px 0px' }}>
              <Col span={4}>{i18n.t('host')}</Col>
              <Col span={4}>{webinar.hostVideo ? i18n.t('on') : i18n.t('off')}</Col>
            </Row>
            <Row style={{ border: '0', padding: '5px 0px' }}>
              <Col span={4}>{i18n.t('panelist')}</Col>
              <Col span={4}>{webinar.panelistsVideo ? i18n.t('on') : i18n.t('off')}</Col>
            </Row>
          </Col>
        </Row>

        <Row
          style={{
            display: 'flex',
            alignItems: 'flex-start',
            padding: '0px 10px 20px 10px'
          }}
        >
          <Col span={6}>{i18n.t('webinarOptions')}</Col>
          <Col span={18}>
            <Row style={{ border: '0', padding: '5px 0px' }}>
              <Col>
                <Icon
                  type={webinar.QnA ? 'check' : 'close'}
                  style={webinar.QnA ? { color: '#4BC800', marginRight: '5px' } : { marginRight: '5px' }}
                />
                {i18n.t('Q&A')}
              </Col>
            </Row>
            <Row style={{ border: '0', padding: '5px 0px' }}>
              <Col>
                <Icon
                  type={webinar.practiceSession ? 'check' : 'close'}
                  style={webinar.practiceSession ? { color: '#4BC800', marginRight: '5px' } : { marginRight: '5px' }}
                />
                {i18n.t('enablePracticeSession')}
              </Col>
            </Row>
            <Row style={{ border: '0', padding: '5px 0px' }}>
              <Col>
                <Icon
                  type={webinar.authenticatedOnly ? 'check' : 'close'}
                  style={webinar.authenticatedOnly ? { color: '#4BC800', marginRight: '5px' } : { marginRight: '5px' }}
                />
                {i18n.t('onlyAuthenticated')}
              </Col>
            </Row>
            <Row style={{ border: '0', padding: '5px 0px' }}>
              <Col>
                <Icon
                  type={webinar.automaticallyRecord ? 'check' : 'close'}
                  style={webinar.automaticallyRecord ? { color: '#4BC800', marginRight: '5px' } : { marginRight: '5px' }}
                />
                {i18n.t('recordAutomatically')}
              </Col>
            </Row>
          </Col>
        </Row>

        <Row style={{ border: '0' }}>
          <Col span={6}>
            <ModalCreateWebinar webinar={webinar} queryWebinarTemplates={this.props.queryWebinarTemplates} />
          </Col>
          <Col span={4}>
            <Button
              type='default'
              onClick={() => this.props.handleChangeDisplay('EDIT', 'Edit')}
            >{i18n.t('editWebinar')}</Button>
          </Col>
          <Col span={4}>
            <Button type='primary'>{i18n.t('startWebinar')}</Button>
          </Col>
        </Row>

        <Tabs className='conferenceTabs' onChange={this.handleChangeTab}>
          <TabPane tab={i18n.t('invitations')} key='0'>
            <InvitationsComponent webinarId={webinar._id} />
          </TabPane>
          {/* <TabPane tab={i18n.t('emailSetting')} key='1'>
            <EmailSettingComponent
              conferenceState={this.props.conferenceState}
            />
          </TabPane> */}
          <TabPane tab={i18n.t('poll')} key='2'>
            <PollComponent webinarId={webinar._id} webinarPolls={this.state.webinarPolls} />
          </TabPane>
          {/* <TabPane tab='Q&A' key='3'>
            <QAComponent />
          </TabPane> */}
        </Tabs>
      </div>
    )
  }
}

export default withTranslation()(DetailWebinar)
