import React from 'react'
import { withTranslation } from 'react-i18next'
import { inject, observer } from 'mobx-react'
import { Input } from 'antd'
import client, { M_SET_FILE_CONFERENCE, Q_ALL_FILES_CONFERENCE } from '@graphql'
import debounce from 'lodash/debounce'
import { newNotification, returnError, parseError } from '@utils'
// import presentationOff from '@misc/images/sharefile-presentation/presentation-off.svg'
// import presentationOn from '@misc/images/sharefile-presentation/presentation-on.svg'
import download from '@misc/images/sharefile-presentation/download.svg'
import closeFile from '@misc/images/sharefile-presentation/close.svg'
import { appConstants } from '@constants'
const { ROLE } = appConstants

const { Search } = Input

@inject(({ stores }) => stores)
@observer
class MyFiles extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      myFiles: [],
      textSearch: ''
    }
    this.handleSearchAllFiles = debounce(this.handleSearchAllFiles, 200)
  }

  handleSearchAllFiles = value => {
    this.setState({ textSearch: value }, () => {
      this.getAllFilesConference()
    })
  }

  getAllFilesConference = async () => {
    const { i18n } = this.props
    client.query({
      query: Q_ALL_FILES_CONFERENCE,
      fetchPolicy: 'no-cache',
      variables: {
        conferenceId: this.props.conferenceState?.conference?._id,
        inputSearch: {
          text: this.state.textSearch
        }
      }
    })
      .then(({ data: { allFilesConference } }) => {
        if (!allFilesConference) {
          throw new Error()
        }
        this.setState({ myFiles: allFilesConference })
      })
      .catch(err => {
        returnError(
          'Error',
          { errorMessage: i18n.t(parseError(err)) },
          { container: 'top-right' }
        )
      })
  }

  setFileConference = async (fileId, input) => {
    const { i18n, conferenceState } = this.props
    client.mutate({
      mutation: M_SET_FILE_CONFERENCE,
      fetchPolicy: 'no-cache',
      variables: {
        conferenceId: conferenceState?.conference?._id,
        fileId,
        input
      }
    })
      .then(({ data: { setFileConference } }) => {
        if (!setFileConference) {
          throw new Error()
        }
        return newNotification(i18n.t('conference.setFileConferenceSuccess'), 'success')
      })
      .catch(err => {
        returnError(
          'Error',
          { errorMessage: i18n.t(parseError(err)) },
          { container: 'top-right' }
        )
      })
  }

  componentDidMount = () => {
    this.getAllFilesConference()
    this.props.conferenceState.setFunc({
      refetchAllFiles: this.getAllFilesConference
    })
  }

  render() {
    const { myFiles } = this.state
    const { conferenceState } = this.props

    return (
      <div className='my-files'>
        <div className='wrapper-control-btn'>
          <Search
            className='search-files'
            placeholder='Tìm tệp tin'
            onChange={({ target: { value } }) => this.handleSearchAllFiles(value)}
          />
        </div>

        <div className='list-item'>
          {!myFiles.length && <div className='no-files'>{this.props.i18n.t('conference.noFileShared')}</div>}

          {myFiles.map(file =>
            <div key={file._id} className='file-item'>
              <div className='name'>
                {file.name}
              </div>

              <div className='action'>

                {/* {file?.isPresentation ?
                  <img
                    src={presentationOn}
                    alt='icon'
                    onClick={() => this.setFileConference(file._id, { isPresentation: false })}
                  />
                  :
                  <img
                    src={presentationOff}
                    alt='icon'
                    style={file.type.includes('image') ? {} : { cursor: 'not-allowed', opacity: 0.5 }}
                    onClick={() => this.setFileConference(file._id, { isPresentation: true })}
                  />
                } */}

                <img src={download} alt='icon' onClick={() => window.open(file.path, '_blank')} />

                <img
                  src={closeFile}
                  alt='icon'
                  style={conferenceState.myRole === ROLE.OWNER || conferenceState.myRole === ROLE.MODERATOR || conferenceState?.myInfo?._id === file?.createdBy?._id ?
                    {
                      visibility: "visible"
                    }
                    :
                    {
                      visibility: "collapse"
                    }
                  }
                  onClick={() => this.setFileConference(file._id, { isShare: false })}
                />

              </div>

            </div>
          )}

        </div>

      </div>
    )
  }
}

export default withTranslation()(MyFiles)
