import React from 'react'
import { Checkbox, Button } from 'antd'
import { inject, observer } from 'mobx-react'

import { client, M_SEND_EMAIL } from '@graphql'

import successIcon from '@misc/images/toolbar/success.svg'
import errorIcon from '@misc/images/toolbar/error.svg'

const STATUS = {
  INVITE: 'INVITE',
  SENDING: 'SENDING',
  SENT: {
    SUCCESS: 'SUCCESS',
    ERROR: 'ERROR'
  }
}


@inject(({ stores }) => stores)
@observer
class TabRemind extends React.PureComponent {
  state = {
    status: STATUS.INVITE,
    selectedEmail: []
  }

  handleSelectEmail = (email) => {
    this.setState(_preState => ({
      selectedEmail: email
    }))
  }

  handleRemindEmail = () => {
    this.setState(_preState => ({
      ..._preState,
      status: STATUS.SENDING
    }))

    client
      .mutate({
        mutation: M_SEND_EMAIL,
        variables: {
          typeSend: 'REMIND',
          typeEmail: 'JOIN_CONFERENCE',
          conferenceId: this.props.conferenceState.conference._id,
          emails: this.state.selectedEmail
        }
      })
      .then(({ data: { sendEmail } }) => {
        this.setState(_preState => ({
          ..._preState,
          status: STATUS.SENT.SUCCESS
        }))
      })
      .catch(error => {
        this.setState(_preState => ({
          ..._preState,
          status: STATUS.SENT.ERROR
        }))
      })
  }

  handleResetInvite = () => {
    this.setState(_preState => ({
      ..._preState,
      status: STATUS.INVITE,
      selectedEmail: []
    }))
  }

  render() {
    const htmlInviteEmail = this.props.inviteEmail.map((item) => {
      return (<Checkbox key={item._id} value={item.email}>{item.email}</Checkbox>)
    })

    const htmlUserEmail = this.props.userEmail.filter(item => item.user !== null).map((item) => {
      const displayEmail = `${item.user.email} (${item.user.firstname} ${item.user.lastname})`
      return (
        <Checkbox
          key={item.user._id}
          value={item.user.email}
        >
          {displayEmail.length > 35 ? displayEmail.slice(0, 35) + '...' : displayEmail}
        </Checkbox>
      )
    })

    const renderElement = {
      [STATUS.INVITE]: (
        < div className='box-invite' >
          <p>The following have not yet joined the meeting. Which invitees should receive meeting reminders?</p>
          <div className='wrapper-checkbox'>
            <Checkbox.Group defaultValue={this.state.selectedEmail} onChange={this.handleSelectEmail}>
              {
                htmlInviteEmail
              }
              {
                htmlUserEmail
              }
            </Checkbox.Group>
          </div>
          <Button
            type='primary'
            disabled={this.state.selectedEmail.length === 0}
            onClick={this.handleRemindEmail}
          >
            Send Reminder
      </Button>
        </div>
      ),
      [STATUS.SENDING]: (
        <div className='box-loading'>
          <div className='spinner'></div>
          <p>Sending Invation</p>
        </div>
      ),
      [STATUS.SENT.SUCCESS]: (
        <div className='box-sent'>
          <img src={successIcon} alt='icon' />
          <span className='success'>{this.state.selectedEmail.length} Invitation was sent</span>
          <div className='group-btn'>
            <Button type='primary' onClick={this.handleResetInvite}>Remind more</Button>
            <Button type='primary' ghost onClick={this.props.handleCancel}>Done</Button>
          </div>
        </div>
      ),
      [STATUS.SENT.ERROR]: (
        <div className='box-sent'>
          <img src={errorIcon} alt='icon' />
          <span className='error'>{this.state.selectedEmail.length} Invitation was not sent</span>
          <div className='group-btn'>
            <Button type='primary' onClick={this.handleResetInvite}>Remind more</Button>
            <Button type='primary' ghost onClick={this.props.handleCancel}>Done</Button>
          </div>
        </div>
      ),
    }

    return (
      <div className='wrapper-remind' >
        {
          renderElement[this.state.status]
        }
      </div>
    )
  }
}

export default TabRemind
