import React from 'react'

import './ParticipantConnectionStats.less'

export default class ParticipantConnectionStats extends React.PureComponent {
  state = { connectionStats: undefined }

  componentDidMount() {
    this.props.setConnectionStats(this.setConnectionStats)
  }

  setConnectionStats = connectionStats => {
    this.setState({ connectionStats })
  }

  render() {
    const { connectionStats } = this.state

    return (
      <div
        className={`connection-filter ${
          connectionStats && connectionStats.connectionQuality !== 0 ? 'hidden' : 'active'
        }`}
      >
        <i />
        <i />
        <i />
      </div>
    )
  }
}
