import React from 'react'
import { Table, Button } from 'antd'
import { withTranslation } from 'react-i18next'
import moment from 'moment'

import ModalDelete from '../components/ModalDelete'

class WebinarTemplate extends React.PureComponent {
  handleScheduleWebinar = (e) => {
    this.props.handleChangeDisplay('CREATE', 'Create webinar')
    this.props.handleChangeSelectedTemplate(e)
  }

  handleDetailTemplate = (e) => {
    this.props.handleChangeDisplay('TEMPLATE', `Manage '${e.name}'`)
    this.props.handleChangeSelectedTemplate(e)
  }

  render() {
    const {
      props: {
        webinarTemplates,
        i18n
      }
    } = this

    const columns = [
      {
        title: i18n.t('templateName'),
        dataIndex: 'name'
      },
      {
        title: i18n.t('modifyTime'),
        dataIndex: 'createdAt'
      },
      {
        title: '',
        render: (e, i) => (
          <span className='columnBtnHolder'>
            <Button onClick={() => this.handleScheduleWebinar(e)} className='btn-start'>{i18n.t('createWithTemplate')}</Button>
            <ModalDelete 
              itemToDelete={i} 
              handleDeleteItem={this.props.handleDeleteTemplate}
              title={i18n.t('deleteTemplate')}
            />
          </span>
        )
      },
    ]

    const data = webinarTemplates.map((e, i) => ({
      key: i,
      name: (
        <span
          className='clickableSpan'
          onClick={() => this.handleDetailTemplate(e)}
        >
          {e.name}
        </span>
      ),
      createdAt: moment(e.startAt).format('MMM Do YYYY, hh:mm a'),
      _id: e._id
    }))

    return (
      <div className='wrapper-webinar'>
        <p>{`You have saved ${webinarTemplates.length} template(s) so far. You can save up to 40 templates.`}</p>
        <Table className='common-table' columns={columns} dataSource={data} />
      </div>
    )
  }
}

export default withTranslation()(WebinarTemplate)
