import React from 'react'
import { withTranslation } from 'react-i18next'
import { withRouter } from 'react-router-dom'
import { inject, observer } from 'mobx-react'
import { client, M_CREATE_VOTE } from '@graphql'
import { newNotification } from '@utils'
import './Attendance.less'

import avatar from '@misc/images/avatar/avatar1.jpg'
import present from '@misc/images/success.svg'
import absent from '@misc/images/error.svg'
import noCheck from '@misc/images/no-check.svg'

@withRouter
@inject(({ stores }) => stores)
@observer
class Attendance extends React.Component {
  state = {
    data: [
      {
        name: 'Trịnh Nhất Sinh',
        score: '1/3',
        firstTime: 'present',
        secondTime: 'absent',
        thirdTime: null
      },
      {
        name: 'Nguyễn Hoàng Trung',
        score: '2/3',
        firstTime: 'present',
        secondTime: 'present',
        thirdTime: null
      },
      {
        name: 'Nguyễn Minh Thông',
        score: '1/3',
        firstTime: 'present',
        secondTime: 'absent',
        thirdTime: null
      }
    ]
  }

  handleCreateAttendance = () => {
    const dataQuestion = 'Attendance'
    const dataAnsers = [
      {
        key: 'A',
        value: 'Present'
      }
    ]
    // MUTATION
    client
      .mutate({
        mutation: M_CREATE_VOTE,
        variables: {
          newPollInput: {
            sessionId: this.props.conferenceState.conference._id,
            question: dataQuestion,
            answers: dataAnsers,
            type: 'Attendance'
          }
        }
      })
      .catch(error => {
        return newNotification('Please try again later!', 'danger')
      })
  }

  render() {
    const {
      props: { handleBack, i18n },
      state: { data },
      handleCreateAttendance
    } = this
    return (
      <>
        <div className='attendance'>
          <div className='attendance-description'></div>
          <div className='attendance-list'>
            {data.map((item, index) => {
              const iconFisrtTime =
                item.firstTime === null
                  ? noCheck
                  : item.firstTime === 'present'
                  ? present
                  : absent
              const iconSecondTime =
                item.secondTime === null
                  ? noCheck
                  : item.secondTime === 'present'
                  ? present
                  : absent
              const iconThirdTime =
                item.thirdTime === null
                  ? noCheck
                  : item.thirdTime === 'present'
                  ? present
                  : absent
              return (
                <div className='attendance-item' key={index}>
                  <div className='attendance-user'>
                    <img src={avatar} alt='icon' />
                    <div className='user-info'>
                      <p className='user-name'>{item.name}</p>
                      <p className='user-score'>{item.score}</p>
                    </div>
                  </div>
                  <div className='first-time'>
                    <img src={iconFisrtTime} alt='icon' />
                  </div>
                  <div className='second-time'>
                    <img src={iconSecondTime} alt='icon' />
                  </div>
                  <div className='third-time'>
                    <img src={iconThirdTime} alt='icon' />
                  </div>
                </div>
              )
            })}
          </div>
        </div>
        <div className='action-box'>
          <button
            type='button'
            className='btn-cancel'
            onClick={() => handleBack('menu')}
          >
            {i18n.t('cancel')}
          </button>
          <button
            type='button'
            className='btn-start'
            onClick={() => handleCreateAttendance()}
          >
            {i18n.t('start')}
          </button>
        </div>
      </>
    )
  }
}

export default withTranslation()(Attendance)
