import React from 'react'
import { inject, observer } from 'mobx-react'

import singleWhite from '@misc/images/menu/single-white.svg'
import singleBlack from '@misc/images/menu/single-black.svg'
import multiWhite from '@misc/images/menu/multi-white.svg'
import multiBlack from '@misc/images/menu/multi-black.svg'
import shareWhite from '@misc/images/menu/share-white.svg'
// import shareBlack from '@misc/images/menu/share-black.svg'

import { LAYOUT_MODEL } from '../constants'

@inject(({ stores }) => stores)
@observer
class LayoutSwitch extends React.PureComponent {
  switchLayout = layoutModel => {
    const { conferenceState } = this.props
    if (LAYOUT_MODEL.SHARE_SLIDE === layoutModel && conferenceState.isPanelOpen) {
      conferenceState.setState({ isPanelOpen: false })
    }
    this.props.jitsiStore.setState({ layoutModel })
  }

  handleChangeActive = (e) => {
    this.refs['layout-active'].style.left = e.currentTarget.offsetLeft + 'px'
  }

  render() {
    const { jitsiStore } = this.props

    return (
      <div className='switch-layout'>
        <div className='show-layout'>
          <div className=''>
            <img
              src={
                jitsiStore.layoutModel === LAYOUT_MODEL.SINGLE
                  ? singleWhite
                  : jitsiStore.layoutModel === LAYOUT_MODEL.SHARE_SLIDE
                    ? shareWhite
                    : multiWhite
              }
              alt='icon'
            />
          </div>
        </div>
        <div className='choose-layout'>
          <div className='layout-active' ref='layout-active'></div>
          {/*  Disable share file */}
          {/* <button
            type='button'
            className={jitsiStore.layoutModel === LAYOUT_MODEL.SHARE_SLIDE ? 'hover-active' : ''}
            onClick={(e) => {
              this.handleChangeActive(e)
              this.switchLayout(LAYOUT_MODEL.SHARE_SLIDE)
            }}
          >
            <img src={jitsiStore.layoutModel === LAYOUT_MODEL.SHARE_SLIDE ? shareWhite : shareBlack} alt='icon' />
          </button> */}
          <button
            type='button'
            className={jitsiStore.layoutModel === LAYOUT_MODEL.MULTI ? 'hover-active' : ''}
            onClick={(e) => {
              this.handleChangeActive(e)
              this.switchLayout(LAYOUT_MODEL.MULTI)
            }}
          >
            <img src={jitsiStore.layoutModel === LAYOUT_MODEL.MULTI ? multiWhite : multiBlack} alt='icon' />
          </button>
          <button
            type='button'
            className={jitsiStore.layoutModel === LAYOUT_MODEL.SINGLE ? 'hover-active' : ''}
            onClick={(e) => {
              this.handleChangeActive(e)
              this.switchLayout(LAYOUT_MODEL.SINGLE)
            }}
          >
            <img src={jitsiStore.layoutModel === LAYOUT_MODEL.SINGLE ? singleWhite : singleBlack} alt='icon' />
          </button>
        </div>
      </div>
    )
  }
}

export default LayoutSwitch
