import React from 'react'

class WrapperResize extends React.PureComponent {
  state = {
    isMouseDown: false,
    startX: 0,
    startY: 0,
    width: 0,
    height: 0
  }

  handleMouseDown = e => {
    e.persist()

    const widthTemp = this.refs['wrapper-local-track'].style.width
    const width = +widthTemp.slice(0, widthTemp.length - 2)

    const heightTemp = this.refs['wrapper-local-track'].style.height
    const height = +heightTemp.slice(0, heightTemp.length - 2)

    this.setState(_preState => ({
      ..._preState,
      isMouseDown: true,
      startX: e.pageX,
      startY: e.pageY,
      width,
      height
    }))

    document.addEventListener('mousemove', this.handleMouseMove)
    document.addEventListener('mouseup', this.handleMouseUp)
  }

  handleMouseMove = e => {
    const newWidth =  e.pageX - this.state.startX + this.state.width
    const newHeight = this.state.startY - e.pageY + this.state.height

    if (newWidth > 150) {
      this.refs['wrapper-local-track'].style.width = newWidth + 'px'
    }

    if (newHeight > 100) {
      this.refs['wrapper-local-track'].style.height = newHeight + 'px'
    }
  }

  handleMouseUp = () => {
    document.removeEventListener('mousemove', this.handleMouseMove, false)
    document.removeEventListener('mouseup', this.handleMouseUp, false)
  }

  render() {
    return (
      <div className='wrapper-local-track' ref='wrapper-local-track' style={{ width: '150px', height: '100px' }}>
        <div className='resize' onMouseDown={this.handleMouseDown}></div>
        {this.props.children}
      </div>
    )
  }
}

export default WrapperResize
