import React from 'react'
import { inject, observer } from 'mobx-react'

import { LAYOUT_MODEL } from '../constants'
import LayoutSingle from './Single'
import LayoutMulti from './Multi'
import LayoutShare from './Share'
import PinUserDesktop from '../PinUser'

@inject(({ stores }) => stores)
@observer
class ReturnLayout extends React.PureComponent {
  render() {
    const { jitsiStore } = this.props

    const renderLayout = {
      [LAYOUT_MODEL.SINGLE]: (
        <LayoutSingle
          mutateSetPinnedUser={this.props.mutateSetPinnedUser}
          //
          tracksOfThisParticipant={this.props.tracksOfThisParticipant}
          doesParticipantHasAudio={this.props.doesParticipantHasAudio}
          doesParticipantHasVideo={this.props.doesParticipantHasVideo}
          //
          updateRemoteStats={this.props.updateRemoteStats}
        />
      ),
      [LAYOUT_MODEL.MULTI]: (
        <LayoutMulti
          mutateSetPinnedUser={this.props.mutateSetPinnedUser}
          //
          tracksOfThisParticipant={this.props.tracksOfThisParticipant}
          doesParticipantHasVideo={this.props.doesParticipantHasVideo}
          doesParticipantHasAudio={this.props.doesParticipantHasAudio}
          //
          updateRemoteStats={this.props.updateRemoteStats}
        />
      )
    }

    switch (jitsiStore.layoutModel) {
      case LAYOUT_MODEL.SINGLE:
      case LAYOUT_MODEL.MULTI: {
        return (
          <>
            <div className='main-content'>
              <div className='speaker-container'>{renderLayout[jitsiStore.layoutModel]}</div>
            </div>

            {jitsiStore.layoutModel === LAYOUT_MODEL.SINGLE && (
              <PinUserDesktop
                setSpeakerByUID={this.props.setSpeakerByUID}
                mutateSetPinnedUser={this.props.mutateSetPinnedUser}
                //
                tracksOfThisParticipant={this.props.tracksOfThisParticipant}
                doesParticipantHasAudio={this.props.doesParticipantHasAudio}
                doesParticipantHasVideo={this.props.doesParticipantHasVideo}
              />
            )}
          </>
        )
      }

      case LAYOUT_MODEL.SHARE_SLIDE: {
        return <LayoutShare />
      }

      default: {
        return <React.Fragment />
      }
    }
  }
}

export default ReturnLayout
