import React from 'react'
import { inject, observer } from 'mobx-react'
import { client, M_DIRECT_JOIN_CONFERENCE } from '@graphql'
import { returnError } from '@utils'
import { withRouter } from 'react-router-dom'

import { BounceLoader } from '@components'

@withRouter
@inject(({ stores }) => stores)
@observer
class Verify extends React.PureComponent {
  componentDidMount() {
    const {
      props: { globalState, match }
    } = this

    // MUTATION: CREATE CONFERENCE
    client
      .mutate({
        mutation: M_DIRECT_JOIN_CONFERENCE,
        variables: {
          token: match.params.ID
        }
      })
      .then(({ data: { directJoinConference } }) => {
        if (directJoinConference) {
          globalState.auth(directJoinConference.accessToken)
          this.props.history.push(
            `/conference/${directJoinConference.conferenceCode}`
          )
        }
      })
      .catch(error =>
        returnError('Verify', {
          error,
          errorMessage: 'Invalid link'
        })
      )
  }

  render() {
    return <BounceLoader />
  }
}

export default Verify
