import React from 'react'
import { withTranslation } from 'react-i18next'
import { Form, Input, Select, TimePicker, DatePicker, Button, Radio, Checkbox } from 'antd'
import moment from 'moment'

import googleCalendarAPI from '../../../calendar/GoogleCalendarAPI'

import { client, M_UPDATE_CONFERENCE } from '@graphql'
import { returnError, newNotification, parseError } from '@utils'
import { observer, inject } from 'mobx-react'

const { Option } = Select


@inject(({ stores }) => stores)
@observer
class FormEditConference extends React.Component {
  state = {
    durationType: 0,
    showPassword: this.props.conference.password ? true : false
  }

  onChange = e => {
    this.setState({ durationType: e.target.value })
  }

  convertTime = (date, time) => {
    const newDate = new Date(
      date._d.getFullYear(),
      date._d.getMonth(),
      date._d.getDate(),
      time._d.getHours(),
      time._d.getMinutes(),
      time._d.getSeconds()
    )
    return newDate.getTime()
  }

  handleSubmit = e => {
    e.preventDefault()
    this.props.form.validateFields((err, values) => {
      const startAt = this.convertTime(values.startDate, values.startTime)
      const duration = (values.hour * 3600 + values.minute * 60) * 1000

      //MUTATION CREATE CONFERENCE
      client
        .mutate({
          mutation: M_UPDATE_CONFERENCE,
          variables: {
            conferenceId: this.props.conference._id,
            newConferenceUpdate: {
              name: values.name,
              description: values.description,
              password: values.password || '',
              startAt,
              duration,
              videoHostDefault: values.hostVideo,
              videoParticipantDefault: values.participantVideo
            }
          }
        })
        .then(({ data: { updateConference } }) => {
          this.props.form.resetFields()
          if (updateConference) {
            this.props.refetch(this.props.conference._id)
            this.props.closeEditFunction()
            newNotification(this.props.i18n.t('notification.updateMeetingSuccess'), 'success', { container: 'top-right' })

            // Google API
            if (this.props.globalState.isGoogleAPI) {
              if (this.props.conference.calendar) {
                googleCalendarAPI.updateEvent(
                  this.props.conference.calendar._id.eventId,
                  values.name,
                  startAt,
                  duration
                )
              }
            }
          }
        })
        .catch(errors => {
          returnError(
            'Error',
            { errorMessage: this.props.i18n.t(parseError(errors)) },
            { container: 'top-right' }
          )
        })
    })
  }

  componentDidMount() {
    const { conference } = this.props
    this.setState({
      durationType: conference ? (conference.duration > 24 * 3600000 ? 1 : 0) : 0
    })
  }

  getMinutes = () => {
    const { conference } = this.props
    const hour = Math.floor(conference.duration / 3600000)
    const minute = Math.floor((conference.duration - hour * 3600000) / 60000)
    return minute
  }

  handleSwitchPassword = e => {
    this.setState({ showPassword: e.target.checked })
  }

  render() {
    const {
      i18n,
      form: { getFieldDecorator },
      conference
    } = this.props

    const initHour = Math.floor(conference.duration / 3600000)
    const initMinute = Math.floor((conference.duration - initHour * 3600000) / 60000)

    const children = []
    for (let i = 1; i <= 24; i++) {
      children.push(<Option key={i}>{i}</Option>)
    }

    return (
      <Form onSubmit={this.handleSubmit} className='form-create-conference' ref='form'>
        <div className='groupInput'>
          <Form.Item required={false} label={i18n.t('meeting.topic')}>
            {getFieldDecorator('name', {
              rules: [{ required: true, message: i18n.t('requireTopic') }],
              initialValue: conference.name
            })(<Input style={{ width: '100%' }} placeholder='My Meeting' />)}
          </Form.Item>
          <Form.Item required={false} label={i18n.t('meeting.description')}>
            {getFieldDecorator('description', {
              initialValue: conference.description
            })(<Input.TextArea style={{ width: '100%' }} placeholder='Enter your meeting description' />)}
          </Form.Item>
        </div>

        <div className='groupInput'>
          <Form.Item label='When'>
            <div style={{ width: '300px' }} className='datePickerWrapper'>
              <div>
                <Form.Item>
                  {getFieldDecorator('startDate', {
                    rules: [{ required: true }],
                    initialValue: moment(conference.startAt)
                  })(<DatePicker placeholder='Select Date' />)}
                </Form.Item>
              </div>
              <div>
                <Form.Item>
                  {getFieldDecorator('startTime', {
                    rules: [{ required: true }],
                    initialValue: moment(conference.startAt)
                  })(<TimePicker placeholder='Select hour' />)}
                </Form.Item>
              </div>
            </div>
          </Form.Item>
          <Form.Item label={i18n.t('meeting.duration')}>
            <div style={{ width: '300px' }} className='durationWrapper'>
              <div>
                <Form.Item>
                  {getFieldDecorator('hour', { initialValue: initHour })(<Select>{children}</Select>)}
                </Form.Item>
                <span>{i18n.t('meeting.hour')}</span>
              </div>

              <div>
                <Form.Item>
                  {getFieldDecorator('minute', { initialValue: initMinute })(
                    <Select>
                      <Option key='0'>00</Option>
                      <Option key='15'>15</Option>
                      <Option key='30'>30</Option>
                      <Option key='45'>45</Option>
                    </Select>
                  )}
                </Form.Item>
                <span>{i18n.t('meeting.minute')}</span>
              </div>
            </div>
          </Form.Item>
        </div>
        <div className='groupInput'>
          <Form.Item label={i18n.t('common.password')}>
            <div style={{ width: '70%' }} className='passwordWrapper'>
              <div>
                <Checkbox checked={this.state.showPassword} onChange={this.handleSwitchPassword}>
                  {i18n.t('require.requirePassword')}
                </Checkbox>
              </div>

              {this.state.showPassword && (
                <Form.Item
                  style={{
                    display: this.state.showPassword ? 'block' : 'none'
                  }}
                >
                  {getFieldDecorator('password', {
                    rules: [{ required: true }],
                    initialValue: conference.password
                  })(<Input style={{ width: '100%' }} placeholder='12345678' />)}
                </Form.Item>
              )}
            </div>
          </Form.Item>
        </div>

        <div className='groupInput'>
          <Form.Item className='videoOption' label='Video'>
            <div style={{ width: '50%' }} className='videoOptionWrapper'>
              <Form.Item label={i18n.t('meeting.host')}>
                {getFieldDecorator('hostVideo', {
                  initialValue: conference.videoHostDefault
                })(
                  <Radio.Group name='radiogroup'>
                    <Radio value={true}>{i18n.t('meeting.on')}</Radio>
                    <Radio value={false}>{i18n.t('meeting.off')}</Radio>
                  </Radio.Group>
                )}
              </Form.Item>
              <Form.Item label={i18n.t('meeting.participant')}>
                {getFieldDecorator('participantVideo', {
                  initialValue: conference.videoParticipantDefault
                })(
                  <Radio.Group name='radiogroup'>
                    <Radio value={true}>{i18n.t('meeting.on')}</Radio>
                    <Radio value={false}>{i18n.t('meeting.off')}</Radio>
                  </Radio.Group>
                )}
              </Form.Item>
            </div>
          </Form.Item>
        </div>

        <div className='btnWrapper'>
          <Form.Item>
            <Button type='primary' htmlType='submit' className='btn-create'>
              {i18n.t('common.save')}
            </Button>
          </Form.Item>
          <Form.Item>
            <Button type='primary' ghost className='btn-cancel' onClick={this.props.closeEditFunction}>
              {i18n.t('common.cancel')}
            </Button>
          </Form.Item>
        </div>
      </Form>
    )
  }
}

export default Form.create({ name: 'normal_login' })(withTranslation()(FormEditConference))
