import React from 'react'
import { withTranslation } from 'react-i18next'
import { withRouter } from 'react-router-dom'
import { inject, observer } from 'mobx-react'
import { client, M_CREATE_TIMER } from '@graphql'
import { newNotification } from '@utils'

import './Timer.less'

import FormTimer from './FormTimer'

@withRouter
@inject(({ stores }) => stores)
@observer
class Timer extends React.Component {
  handleStart = () => {
    this.refs.form.validateFields((err, values) => {
      if (!err) {
        const timeStamp = (values.minute * 60 + values.seconds) * 1000
        // MUTATION CREATE VOTE
        client
          .mutate({
            mutation: M_CREATE_TIMER,
            variables: {
              newTimerInput: {
                conferenceId: this.props.conferenceState.conference._id,
                amountOfTime: timeStamp,
                descriptions: values.description,
                type: 'Timer'
              }
            }
          })
          .catch(error => {
            return newNotification('Please try again later!', 'danger')
          })
        this.props.handleBack('menu')
      }
    })
  }
  render() {
    const {
      props: { handleBack, i18n },
      handleStart
    } = this
    return (
      <>
        <div className='form-box'>
          <FormTimer ref='form' />
        </div>
        <div className='action-box'>
          <button
            type='button'
            className='btn-cancel'
            onClick={() => handleBack('menu')}
          >
            {i18n.t('cancel')}
          </button>
          <button
            type='button'
            className='btn-start'
            onClick={handleStart}
          >
            {i18n.t('start')}
          </button>
        </div>
      </>
    )
  }
}

export default withTranslation()(Timer)
