import React from 'react'
import { withRouter } from 'react-router-dom'

import MessageList from './ChatComponents/MessageListSection'
import MessagesSection from './ChatComponents/MessagesSection'

export const constant = {
  MESSAGE_LIST: 'MESSAGE_LIST',
  MESSAGES: 'MESSAGES'
}

@withRouter
class TabChat extends React.PureComponent {
  state = {
    currentSection: constant.MESSAGE_LIST,
    messageList: [],
    withUser: null
  }

  switchSection = (sectionKey, withUser) => {
    if (sectionKey === constant.MESSAGES) {
      this.setState({
        currentSection: constant.MESSAGES,
        withUser
      })
    } else {
      this.setState({ currentSection: constant.MESSAGE_LIST })
      this.props.handleCloseChat()
    }
  }

  render() {
    return (
      <div className='tabchat-container'>
        {this.props.selectedUser ? (
          <MessagesSection
            conferenceId={this.props.conferenceId}
            switchSection={this.switchSection}
            withUser={this.props.selectedUser}
          />
        ) : this.state.currentSection === constant.MESSAGE_LIST ? (
          <MessageList
            conferenceId={this.props.conferenceId}
            switchSection={this.switchSection}
          />
        ) : (
          <MessagesSection
            conferenceId={this.props.conferenceId}
            switchSection={this.switchSection}
            withUser={this.state.withUser}
          />
        )}
      </div>
    )
  }
}

export default TabChat
