import React from 'react'
import { Dropdown, Button, Tooltip, Menu, Icon, Popover } from 'antd'
import { withRouter } from 'react-router-dom'
import { inject, observer } from 'mobx-react'
import { X } from 'react-feather'

import './ToolBar.less'

import { client, Q_HAS_POLLING, M_END_POLL, M_STOP_STREAM, M_LOCK_OR_UNLOCK_CONFERENCE, M_ATTEND } from '@graphql'
import { appConstants, _tabs } from '@constants'
import { newNotification, returnError, returnNotification } from '@utils'
import fileIcon from '@misc/images/toolbar/file.svg'

// NOTE: Toolbar item icon
// import chart from '@misc/images/chart.svg'
import userBtn from '@misc/images/toolbar/participant.svg'
import messageBtn from '@misc/images/toolbar/chat.svg'
import recIcon from '@misc/images/toolbar/rec.svg'
import endRecIcon from '@misc/images/toolbar/endrecording.svg'
import moreIcon from '@misc/images/toolbar/Vector.svg'
import copyLink from '@misc/images/toolbar/copy.svg'
import permissionIcon from '@misc/images/toolbar/permission.svg'
import pollIcon from '@misc/images/toolbar/poll.svg'
import settingIcon from '@misc/images/toolbar/setting.svg'
// import noSoundBlack from '@misc/images/action/no-sound_black.svg'
import livestreamIcon from '@misc/images/toolbar/livestream.svg'
import inviteIcon from '@misc/images/toolbar/invite-remind.svg'
import lockIcon from '@misc/images/ngocly/lock.svg'

// NOTE: Additional components
// import ModalVote from './modals/ModalVote'
// import ModalAttendance from './modals/ModalAttendance'
import ButtonCamera from './ButtonCamera'
import ButtonMicro from './ButtonMicro'
import ButtonShare from './ButtonShare'
import ModalShareScreen from '../modals/ModalShareScreen'
import ModalPermission from '../modals/ModalPermission'
import ModalResource from '../modals/ModalResource'
// import ModalResource from '../modals/ModalResource'
// import ModalSetting from '../modals/ModalSetting'
import ModalDevicesSetting from '../modals/DevicesSetting'
import ModalInvite from '../modals/ModalInvite'
import { isElectron } from './utils'
import { checkPermission } from '@constants'
import DropdownRecord from './components/DropdownRecord'
import ModalConfirmStopRecord from '../modals/ModalConfirmStopRecord'

const { ROLE, TOOLBAR } = appConstants

@inject(({ stores }) => stores)
@observer
class ToolBar extends React.PureComponent {
  // PUBSUB:
  subscriber = null

  state = {
    pollModalVisbile: false,
    setIntervalId: null,
    shareContentVisible: false,
    visibleTooltipContent: false,

    // PUBSUB:
    hasPolling: false,
    poll: null,
    openMore: false,

    attendTooltip: false
  }

  isConferenceOwner = () => this.props.conferenceState.myRole === ROLE.OWNER

  attendedYet = () => {
    const {
      props: {
        conferenceState: {
          attendanceInfo: { userAttendances },
          myInfo
        }
      }
    } = this

    return userAttendances.map(e => e.user._id).includes(myInfo._id)
  }

  handleStopLiveStream = async () => {
    await client
      .mutate({
        mutation: M_STOP_STREAM,
        variables: {
          conferenceId: this.props.conferenceState.conference._id
        }
      })
      .then(res => {
        if (res && res.data && res.data.stopStream) {
          return newNotification('Đã tắt phát trực tuyến!', 'success')
        }
        throw new Error('Có lỗi xảy ra!')
      })
      .catch(err => {
        return newNotification(err.message, 'danger')
      })
  }

  queryHasPolling = () =>
    client
      .query({
        query: Q_HAS_POLLING,
        variables: { conferenceId: this.props.conferenceId },
        fetchPolicy: 'network-only'
      })
      .then(({ data: { hasPolling } }) => {
        if (hasPolling.length > 0) {
          this.handleCountDownAttendance(hasPolling)
          this.setState({ hasPolling: true, pollInfo: hasPolling })
          this.props.conferenceState.setState({ hasPolling: true })
        } else {
          this.handleCountDownAttendance(null)
          this.setState({ hasPolling: false, pollInfo: null })
          this.props.conferenceState.setState({ hasPolling: false })
        }
      })
      .catch(error =>
        returnError('hasPolling', {
          error,
          errorMessage: 'Cannot get polling data!'
        })
      )

  handleClickOutside = event => {
    event.stopPropagation()
    if (
      this.refs.more &&
      !this.refs.more.contains(event.target) &&
      this.state.openMore &&
      !this.props.conferenceState.isShowPermission
    ) {
      this.handleChangeMore()
    }

    if (
      this.refs.shareContentDropdown &&
      !this.refs.shareContentDropdown.contains(event.target) &&
      this.state.shareContentVisible
    ) {
      this._handleChangeShareContent()
    }
  }

  componentDidMount() {
    document.addEventListener('mousedown', this.handleClickOutside)

    // QUERY:
    this.queryHasPolling()

    // PUBSUB:
    // this.subscriber = client
    //   .subscribe({
    //     query: S_ON_VOTING,
    //     variables: { conferenceId: this.props.conferenceId },
    //     fetchPolicy: 'network-only'
    //   })
    //   .subscribe(({ data: { onVoting } }) => {
    //     if (!onVoting.isActive) {
    //       this.handleCountDownAttendance(null)
    //       this.setState({ hasPolling: false, pollInfo: null })
    //     } else {
    //       this.handleCountDownAttendance(onVoting)
    //       this.setState({ hasPolling: true, pollInfo: onVoting })
    //     }
    //     this.handleCountDownAttendance()
    //   })
  }

  componentWillUnmount() {
    // PUBSUB:
    if (this.subscriber) {
      this.subscriber.unsubscribe()
      this.subscriber = null
    }
  }

  toggleFullscreen = e => {
    let element = document.documentElement
    if (e instanceof HTMLElement) {
      element = e
    }

    const isFullscreen = document.webkitIsFullScreen || document.mozFullScreen || false

    element.requestFullScreen =
      element.requestFullScreen || element.webkitRequestFullScreen || element.mozRequestFullScreen

    document.cancelFullScreen =
      document.cancelFullScreen || document.webkitCancelFullScreen || document.mozCancelFullScreen

    isFullscreen ? document.cancelFullScreen() : element.requestFullScreen()
  }

  handleToggleModalVote = value => {
    this.setState({
      pollModalVisbile: !value,
      visibleProfile: false
    })
  }

  checkSelectAttendance = () => {
    const { pollInfo } = this.state

    if (pollInfo) {
      const result = pollInfo.userVotes.filter(item => item.user._id === this.props.conferenceState.myUserId)
      return !(result.length === 1 && pollInfo.type === 'Attendance')
    }

    return true
  }

  handleCountDownAttendance = pollInfo => {
    const { setIntervalId } = this.state

    if (pollInfo && pollInfo.type === 'Attendance' && pollInfo.createdBy._id === this.props.conferenceState.myUserId) {
      const endTime = pollInfo.createdAt + 30 * 1000
      clearInterval(setIntervalId)
      const idTemp = setInterval(() => {
        if (Date.now() >= endTime) {
          clearInterval(idTemp)
          // MUTATION: END ATTENDANCE
          client
            .mutate({
              mutation: M_END_POLL,
              variables: { pollId: pollInfo._id }
            })
            .catch(error =>
              returnError('endPoll', {
                error,
                errorMessage: 'Please try again later!'
              })
            )
        }
      }, 1000)
      this.setState(_preState => ({
        setIntervalId: idTemp
      }))
    }
  }

  handleDropdownSelect = ({ key }) => {
    switch (key) {
      case TOOLBAR.DEVICES_SETTING: {
        if (typeof this.showModalDevicesSetting === 'function') return this.showModalDevicesSetting()

        break
      }

      case TOOLBAR.INVITE_REMIND: {
        return this.showModalInvite()
      }

      case TOOLBAR.PERMISSIONS_SETTING: {
        return this.showModalPermission()
      }

      default: {
      }
    }
  }

  copyClipboard = () => {
    const el = document.createElement('textarea')
    el.value = document.URL
    document.body.appendChild(el)
    el.select()
    document.execCommand('copy')
    document.body.removeChild(el)
    returnNotification('The meeting link has been copied to your clipboard', 'success')
  }

  handleIsChecked = () => {
    if (this.state.pollInfo) {
      const result = this.state.pollInfo.userVotes.filter(item => item.user._id === this.props.conferenceState.myUserId)
      return result.length !== 0
    }
    return false
  }

  mutateLockConference = () => {
    const noti = this.props.conferenceState.conference.isLocked
      ? 'Conference has been unlocked'
      : 'Conference has been locked'
    client
      .mutate({
        mutation: M_LOCK_OR_UNLOCK_CONFERENCE,
        variables: {
          conferenceId: this.props.conferenceId
        }
      })
      .then(({ data: { toggleLockConference } }) => {
        if (toggleLockConference) {
          returnNotification(noti, 'info', { container: 'top-right' })
        }
      })
      .catch(err => console.log(err))
  }

  onLeave = e => {
    this.props.conferenceState.LEAVE()
  }

  onClickParticipants = () => {
    const {
      isShowMessages,
      isShowPoll,
      isShowAttendancesManagement,
      currentTab,
      setState,
      isShowPollManagement,
      isShowQnAManagement,
      isShowQnA
    } = this.props.conferenceState

    if (
      !isShowMessages &&
      !isShowPoll &&
      !isShowPollManagement &&
      !isShowAttendancesManagement &&
      !isShowQnAManagement &&
      !isShowQnA
    ) {
      setState({
        isPanelOpen: true,
        isShowParticipants: true,
        currentTab: currentTab === _tabs.PARTICIPANT ? '' : _tabs.PARTICIPANT
      })
    } else {
      setState({
        isShowParticipants: true,
        currentTab: currentTab === _tabs.PARTICIPANT ? '' : _tabs.PARTICIPANT
      })
    }
  }

  onClickMessages = () => {
    const {
      isShowParticipants,
      isShowPoll,
      isShowAttendancesManagement,
      currentTab,
      setState,
      isShowPollManagement,
      isShowQnAManagement,
      isShowQnA
    } = this.props.conferenceState
    if (
      !isShowParticipants &&
      !isShowPoll &&
      !isShowPollManagement &&
      !isShowAttendancesManagement &&
      !isShowQnAManagement &&
      !isShowQnA
    ) {
      setState({
        isPanelOpen: true,
        isShowMessages: true,
        currentTab: currentTab === _tabs.CHAT ? '' : _tabs.CHAT
      })
    } else {
      setState({ isShowMessages: true, currentTab: currentTab === _tabs.CHAT ? '' : _tabs.CHAT })
    }
  }

  onClickPoll = () => {
    const {
      isShowParticipants,
      isShowMessages,
      isShowAttendancesManagement,
      currentTab,
      setState,
      isShowPollManagement,
      isShowQnAManagement,
      isShowQnA
    } = this.props.conferenceState
    if (
      !isShowParticipants &&
      !isShowMessages &&
      !isShowPollManagement &&
      !isShowAttendancesManagement &&
      !isShowQnAManagement &&
      !isShowQnA
    ) {
      setState({
        isPanelOpen: true,
        isShowPoll: true,
        currentTab: currentTab === _tabs.POLL ? '' : _tabs.POLL
      })
    } else {
      setState({ isShowPoll: true, currentTab: currentTab === _tabs.POLL ? '' : _tabs.POLL })
    }
  }

  onClickPollManagement = () => {
    const {
      isShowParticipants,
      isShowMessages,
      isShowPoll,
      isShowAttendancesManagement,
      currentTab,
      setState,
      isShowQnAManagement,
      isShowQnA
    } = this.props.conferenceState
    if (
      !isShowParticipants &&
      !isShowMessages &&
      !isShowPoll &&
      !isShowAttendancesManagement &&
      !isShowQnAManagement &&
      !isShowQnA
    ) {
      setState({
        isPanelOpen: true,
        isShowPollManagement: true,
        currentTab: currentTab === _tabs.POLL_MANAGEMENT ? '' : _tabs.POLL_MANAGEMENT
      })
    } else {
      setState({
        isShowPollManagement: true,
        currentTab: currentTab === _tabs.POLL_MANAGEMENT ? '' : _tabs.POLL_MANAGEMENT
      })
    }
  }

  onClickAttendanceManagement = () => {
    const {
      isShowParticipants,
      isShowMessages,
      isShowPoll,
      currentTab,
      setState,
      isShowPollManagement,
      isShowQnAManagement,
      isShowQnA
    } = this.props.conferenceState
    if (
      !isShowParticipants &&
      !isShowMessages &&
      !isShowPoll &&
      !isShowPollManagement &&
      !isShowQnAManagement &&
      !isShowQnA
    ) {
      setState({
        isPanelOpen: true,
        isShowAttendancesManagement: true,
        currentTab: currentTab === _tabs.ATTENDANCE_MANAGEMENT ? '' : _tabs.ATTENDANCE_MANAGEMENT
      })
    } else {
      setState({
        isShowAttendancesManagement: true,
        currentTab: currentTab === _tabs.ATTENDANCE_MANAGEMENT ? '' : _tabs.ATTENDANCE_MANAGEMENT
      })
    }
  }

  onClickQnAManagement = () => {
    const {
      isShowParticipants,
      isShowMessages,
      isShowPoll,
      currentTab,
      setState,
      isShowPollManagement,
      isShowAttendancesManagement,
      isShowQnA
    } = this.props.conferenceState

    if (
      !isShowParticipants &&
      !isShowMessages &&
      !isShowPoll &&
      !isShowPollManagement &&
      !isShowAttendancesManagement &&
      !isShowQnA
    ) {
      setState({
        isPanelOpen: true,
        isShowQnAManagement: true,
        currentTab: currentTab === _tabs.QNA_MANAGEMENT ? '' : _tabs.QNA_MANAGEMENT
      })
    } else {
      setState({
        isShowQnAManagement: true,
        currentTab: currentTab === _tabs.QNA_MANAGEMENT ? '' : _tabs.QNA_MANAGEMENT
      })
    }
  }

  onClickQnA = () => {
    const {
      isShowParticipants,
      isShowMessages,
      isShowPoll,
      currentTab,
      setState,
      isShowPollManagement,
      isShowAttendancesManagement,
      isShowQnAManagement
    } = this.props.conferenceState

    if (
      !isShowParticipants &&
      !isShowMessages &&
      !isShowPoll &&
      !isShowPollManagement &&
      !isShowAttendancesManagement &&
      !isShowQnAManagement
    ) {
      setState({
        isPanelOpen: true,
        isShowQnA: true,
        currentTab: currentTab === _tabs.QNA ? '' : _tabs.QNA
      })
    } else {
      setState({
        isShowQnA: true,
        currentTab: currentTab === _tabs.QNA ? '' : _tabs.QNA
      })
    }
  }


  onClickShareFile = () => {
    const {
      isShowMessages,
      isShowPoll,
      isShowAttendancesManagement,
      currentTab,
      setState,
      isShowPollManagement,
      isShowQnAManagement,
      isShowQnA,
      isShowParticipants
    } = this.props.conferenceState

    if (
      !isShowMessages &&
      !isShowPoll &&
      !isShowPollManagement &&
      !isShowAttendancesManagement &&
      !isShowQnAManagement &&
      !isShowQnA &&
      !isShowParticipants
    ) {
      setState({
        isPanelOpen: true,
        isShowShareFile: true,
        currentTab: currentTab === _tabs.SHARE_FILE ? '' : _tabs.SHARE_FILE
      })
    } else {
      setState({
        isShowShareFile: true,
        currentTab: currentTab === _tabs.SHARE_FILE ? '' : _tabs.SHARE_FILE
      })
    }
  }


  handleChangeMore = () => {
    this.setState(_preState => ({
      openMore: !_preState.openMore
    }))
  }

  _handleChangeShareContent = () => {
    this.setState(prev => ({ shareContentVisible: !prev.shareContentVisible }))
  }

  handleAttend = () => {
    const {
      props: {
        conferenceState: { attendanceInfo }
      }
    } = this
    client
      .mutate({
        mutation: M_ATTEND,
        variables: {
          attendanceId: attendanceInfo._id
        }
      })
      .then(e => {
        return newNotification('Thành công! ', 'success', {
          animationIn: ['animated', 'fadeIn']
        })
      })
      .catch(error => {
        returnError('vote', {
          error,
          errorMessage: 'Please try again later!'
        })
      })
  }

  componentDidUpdate(_prevProps, prevState) {
    if (prevState.hasPolling === false && this.state.hasPolling === true) {
      if (!this.props.conferenceState.isShowPoll) {
        this.onClickPoll()
        if (this.props.conferenceState.pollsInfo.length && this.props.conferenceState.pollsInfo[0].isPublic) {
          this.props.conferenceState.setState({ isShowPollResult: true })
        }
      }
    }
  }

  render() {
    const {
      jitsiStore,
      conferenceState,
      onAudio,
      offAudio,
      onCamera,
      offCamera,
      onShareScreen,
      offShareScreen,
      openStreamKeyDialog
    } = this.props

    return (
      <div
        className='toolbar'
        style={{
          bottom:
            this.props.jitsiStore.participants.length && this.props.jitsiStore.layoutModel === 'SINGLE'
              ? '120px'
              : '17px',
          opacity: this.props.jitsiStore.isMouseMove ? '1' : '0'
        }}
      >
        <div className='wrap-box'>
          {/*  *: Audio */}
          {checkPermission(conferenceState.myPermissions, 'SHARE_VOICE') ? (
            <ButtonMicro onAudio={onAudio} offAudio={offAudio} />
          ) : (
              <React.Fragment />
            )}

          {/* *: Camera */}
          {checkPermission(conferenceState.myPermissions, 'SHARE_CAMERA') ? (
            <ButtonCamera onCamera={onCamera} offCamera={offCamera} />
          ) : (
              <React.Fragment />
            )}

          {/* Share Screen */}
          {checkPermission(conferenceState.myPermissions, 'SHARE_SCREEN') ? (
            !isElectron() ? (
              <ButtonShare
                onShareScreen={onShareScreen}
                offShareScreen={offShareScreen}
              // onClickShareFile={this.onClickShareFile}
              />
            ) : (
                <ModalShareScreen
                  isShareScreen={jitsiStore.isShareScreen}
                  onShareScreen={onShareScreen}
                  offShareScreen={offShareScreen}
                />
              )
          ) : (
              <React.Fragment />
            )}

          {/* Permission */}
          {checkPermission(conferenceState.myPermissions, 'AUTHORIZE') ? (
            <ModalPermission showModalPermission={callback => (this.showModalPermission = callback)} />
          ) : (
              <React.Fragment />
            )}

          <ModalInvite showDialog={callback => (this.showModalInvite = callback)} />

          <ModalResource />

          <ModalDevicesSetting handleOpen={callback => (this.showModalDevicesSetting = callback)} />

          <ModalConfirmStopRecord showDialog={callback => (this.showModalConfirmStopRecord = callback)} />

          {/* Record */}

          {this.isConferenceOwner() && (
            <div className='action-item action-item-micro'>
              <Dropdown
                overlayClassName='dropdown-record'
                overlay={<DropdownRecord showModalConfirmStopRecord={this.showModalConfirmStopRecord} />}
                trigger={['click']}
                placement='topCenter'
              >
                <Button type='primary'>
                  <img src={this.props.conferenceState.recording ? endRecIcon : recIcon} alt='icon' />
                </Button>
              </Dropdown>
            </div>
          )}

          {/*Participants*/}
          <div className='action-item action-item-micro'>
            <Tooltip title='Participants'>
              <Button
                type='primary'
                onClick={this.onClickParticipants}
                style={{
                  background: conferenceState.currentTab === _tabs.PARTICIPANT ? '#5D9C5A' : '#535353'
                }}
              >
                <img src={userBtn} alt='icon' />
              </Button>
            </Tooltip>
          </div>

          {/*Messages*/}
          {checkPermission(conferenceState.myPermissions, 'CHAT') ? (
            <div className='action-item action-item-micro'>
              {conferenceState.unreadMessage && conferenceState.unreadMessage > 0 ? (
                <div className='unread'>{conferenceState.unreadMessage}</div>
              ) : (
                  <React.Fragment />
                )}
              <Tooltip title='Messages'>
                <Button
                  type='primary'
                  onClick={this.onClickMessages}
                  style={{
                    background: conferenceState.currentTab === _tabs.CHAT ? '#5D9C5A' : '#535353'
                  }}
                >
                  <img src={messageBtn} alt='icon' />
                </Button>
              </Tooltip>
            </div>
          ) : (
              <React.Fragment />
            )}

          {/* Polling */}
          {checkPermission(conferenceState.myPermissions, 'VOTE') && conferenceState.hasPolling ? (
            <div className='action-item action-item-micro'>
              <Tooltip title='Poll'>
                <Button
                  type='primary'
                  onClick={this.onClickPoll}
                  style={{
                    background: conferenceState.currentTab === _tabs.POLL ? '#5D9C5A' : '#535353'
                  }}
                >
                  <img src={pollIcon} alt='icon' />
                </Button>
              </Tooltip>
            </div>
          ) : (
              <React.Fragment />
            )}

          {/* Attendances */}
          {// checkPermission(conferenceState.myPermissions, 'VOTE') &&
            conferenceState.attendanceInfo ? (
              <div
                className={`action-item  ${
                  conferenceState.attendanceInfo && !this.attendedYet() && !conferenceState.isShowAttendance
                    ? 'action-item-attend'
                    : conferenceState.isShowAttendance
                      ? 'action-item-green-bg'
                      : ''
                  }`}
              >
                <Popover
                  content={
                    <div
                      style={{
                        display: 'flex',
                        justifyContent: 'center',
                        alignItems: 'center'
                      }}
                    >
                      <Button disabled={this.attendedYet()} type='primary' onClick={this.handleAttend}>
                        {this.attendedYet() ? 'Đã Điểm Danh' : 'Điểm Danh'}
                      </Button>
                    </div>
                  }
                  title={`Điểm danh lần ${conferenceState.doneAttendances.length + 1}`}
                  trigger='click'
                  visible={conferenceState.isShowAttendance && jitsiStore.isMouseMove}
                  onVisibleChange={isShowAttendance => {
                    return conferenceState.setState({ isShowAttendance })
                  }}
                >
                  <Tooltip
                    onVisibleChange={attendTooltip => {
                      return this.setState({ attendTooltip })
                    }}
                    visible={!conferenceState.isShowAttendance && this.state.attendTooltip}
                    title='Attendance'
                  >
                    <Button type='primary'>
                      <Icon type='carry-out' />
                    </Button>
                  </Tooltip>
                </Popover>
              </div>
            ) : (
                <React.Fragment />
              )}

          {/* QnA */}
          {conferenceState.hasQnA ? (
            <div
              className={`action-item  ${
                conferenceState.hasQnA && !conferenceState.isShowQnA
                  ? 'action-item-attend'
                  : conferenceState.isShowQnA
                    ? 'action-item-green-bg'
                    : ''
                }`}
            >
              <Tooltip title='Q&A'>
                <Button
                  type='primary'
                  onClick={this.onClickQnA}
                  style={{
                    background: conferenceState.currentTab === _tabs.QNA ? '#5D9C5A' : '#535353'
                  }}
                >
                  <Icon type='question' />
                </Button>
              </Tooltip>
            </div>
          ) : (
              <React.Fragment />
            )}

          {/* More */}
          <div className='action-item action-item-micro' ref='more'>
            <Dropdown
              overlay={
                <Menu
                  className='toolbar-more-container'
                  // selectedKeys={[this.props.conferenceState.conference.mutePolicy]}
                  onClick={this.handleDropdownSelect}
                >
                  {checkPermission(conferenceState.myPermissions, 'AUTHORIZE') && this.isConferenceOwner() ? (
                    <Menu.Item key={TOOLBAR.PERMISSIONS_SETTING}>
                      <button type='button'>
                        <img src={permissionIcon} alt='icon' />
                        <span>Permission</span>
                      </button>
                    </Menu.Item>
                  ) : (
                      <React.Fragment />
                    )}
                  {this.isConferenceOwner() && (
                    <Menu.Item onClick={this.mutateLockConference}>
                      <button type='button'>
                        <img src={lockIcon} alt='icon' />
                        <span>{conferenceState.conference.isLocked ? 'Unlock conference' : 'Lock conference'}</span>
                      </button>
                    </Menu.Item>
                  )}
                  <Menu.Item key={TOOLBAR.INVITE_REMIND}>
                    <button type='button'>
                      <img src={inviteIcon} alt='icon' />
                      <span>Invite and remind</span>
                    </button>
                  </Menu.Item>

                  {checkPermission(conferenceState.myPermissions, 'VOTE_CREATE') && (
                    <Menu.Item onClick={this.onClickPollManagement}>
                      <button type='button'>
                        {/* <img src={pollIcon} alt='icon' /> */}
                        <Icon type='project' />
                        <span>Poll Management</span>
                      </button>
                    </Menu.Item>
                  )}

                  {// checkPermission(conferenceState.myPermissions, 'VOTE_CREATE') &&
                    this.isConferenceOwner() && (
                      <Menu.Item onClick={this.onClickAttendanceManagement}>
                        <button type='button'>
                          <Icon type='team' />
                          <span>Attendance Management</span>
                        </button>
                      </Menu.Item>
                    )}

                  {// checkPermission(conferenceState.myPermissions, 'VOTE_CREATE') &&
                    this.isConferenceOwner() && (
                      <Menu.Item onClick={this.onClickQnAManagement}>
                        <button type='button'>
                          <Icon type='profile' />
                          <span>QnA Management</span>
                        </button>
                      </Menu.Item>
                    )}


                  <Menu.Item onClick={this.onClickShareFile}>
                    <button type='button'>
                      <img src={fileIcon} alt='icon' />
                      <span>Sharing Files Management</span>
                    </button>
                  </Menu.Item>


                  {this.isConferenceOwner() && (
                    <Menu.Item onClick={this.copyClipboard}>
                      <button type='button'>
                        <img src={copyLink} alt='icon' />
                        <span>Copy meeting link</span>
                      </button>
                    </Menu.Item>
                  )}
                  <Menu.Item key={TOOLBAR.DEVICES_SETTING}>
                    <button type='button'>
                      <img src={settingIcon} alt='icon' />
                      <span>Speaker, microphone</span>
                    </button>
                  </Menu.Item>

                  {this.isConferenceOwner() && (
                    <Menu.Item>
                      {conferenceState.conference.isRecording ? (
                        <button type='button' onClick={this.handleStopLiveStream}>
                          <img src={livestreamIcon} alt='icon' />
                          <span>Stop Live Stream</span>
                        </button>
                      ) : (
                          <button type='button' onClick={openStreamKeyDialog}>
                            <img src={livestreamIcon} alt='icon' />
                            <span>Live Stream</span>
                          </button>
                        )}
                    </Menu.Item>
                  )}
                </Menu>
              }
              trigger={['click']}
              placement='topCenter'
            >
              <Button
                type='primary'
                style={this.state.openMore ? { transform: 'rotate(90deg)' } : { transform: 'rotate(0deg)' }}
                onClick={this.handleChangeMore}
              >
                <img src={moreIcon} alt='icon' />
              </Button>
            </Dropdown>
          </div>

          {/* Leave */}
          <div className='action-item'>
            <Tooltip title='Leave Conference'>
              <Button type='danger' style={{ background: 'red' }} onClick={this.onLeave}>
                <X />
              </Button>
            </Tooltip>
          </div>
        </div>
      </div>
    )
  }
}

export default withRouter(ToolBar)
