import React, { Component } from 'react'
import { Col, Row } from 'antd'
import ChangePanelistsModalWrapped from './ChangePanelistsModal'

export default class InvitationsComponent extends Component {
  state = {
    panelists: [],
    showChangePanelistsModal: false
  }
  handleChangePanelists = e => {
    this.setState({ panelists: e })
  }

  showChangePanelistsModal = () => {
    this.setState({ showChangePanelistsModal: true })
  }

  closeChangePanelistsModal = () => {
    this.setState({ showChangePanelistsModal: false })
  }
  render() {
    const { panelists, showChangePanelistsModal } = this.state

    return (
      <div className='invitationsComp'>
        <Row>
          <Col className='gutter-row' span={4}>
            <div className='gutter-box'>Invite panelists</div>
          </Col>
          <Col className='gutter-row' span={20}>
            <div className='gutter-box'>
              {panelists.map((e, i) => (
                <div key={i}>
                  <span style={{ fontWeight: 700 }}>{e.name} </span>
                  <span>({e.email})</span>
                </div>
              ))}
              <span
                className='clickableSpan'
                onClick={this.showChangePanelistsModal}
              >
                Edit
              </span>
            </div>
          </Col>
        </Row>

        <Row>
          <Col className='gutter-row' span={4}>
            <div className='gutter-box'>Invite Attendees</div>
          </Col>
        </Row>
        <ChangePanelistsModalWrapped
          showChangePanelistsModal={showChangePanelistsModal}
          handleChangePanelists={this.handleChangePanelists}
          closeChangePanelistsModal={this.closeChangePanelistsModal}
          webinarId={this.props.webinarId}
        />
      </div>
    )
  }
}
