import React from 'react'
import { withTranslation } from 'react-i18next'
import { Modal, Button } from 'antd'
import './ButtonMore.less'

import { AvatarComponent } from '@components'

class ButtonMore extends React.PureComponent {
  state = { visible: false }

  showModal = () => {
    this.setState({ visible: true })
  }

  handleCancel = () => {
    this.setState({ visible: false })
  }

  render() {
    const {
      conferenceUser,
      i18n
    } = this.props
    return (
      <>
        <button className='user-conference-more' onClick={this.showModal}>
          {i18n.t('meeting.allMembers')}
        </button>
        <Modal
          className='common-modal'
          title={i18n.t('meeting.allMembers')}
          visible={this.state.visible}
          footer={null}
          onCancel={this.handleCancel}
        >
          <div className='wrapper-conference-user'>
            {
              conferenceUser ? conferenceUser.map(({ user }, index) => (
                <div key={index} className='conference-user-more-item'>
                  <AvatarComponent {...user} />
                  <span>{`${user.firstname} ${user.lastname}`}</span>
                </div>
              )) : null
            }
          </div>
          <div className='wrapper-button'>
            <Button type='primary' onClick={this.handleCancel}>Ok</Button>
            <Button type='primary' ghost onClick={this.handleCancel}>
              {i18n.t('common.cancel')}
            </Button>
          </div>
        </Modal>
      </>
    )
  }
}

export default withTranslation()(ButtonMore)
