import React from 'react'
import { inject, observer } from 'mobx-react'

import SpeakerTrack from '../SpeakerTrack'
import RemoteTracks from '../RemoteTracks'
import LocalTrack from '../LocalTrack'
import { isBotRecord } from '@utils'

@inject(({ stores }) => stores)
@observer
class Single extends React.PureComponent {

  render() {
    const { conferenceState } = this.props
    const profiles = conferenceState?.myInfo?.profiles
    const isBot = isBotRecord(profiles)

    return (
      <div className='speaker-wrapper'>
        <div className='speaker-wrapper-right'>
          {this.props.jitsiStore.participants.length || this.props.jitsiStore.speaker ? (
            <SpeakerTrack />
          ) : (
              <div className='speaker-no-remote-tracks'>
                <span>Waiting for others to join</span>
              </div>
            )}
        </div>

        {!isBot && (
          <div className='speaker-wrapper-bottom'>
            <LocalTrack mutateSetPinnedUser={this.props.mutateSetPinnedUser} />
          </div>
        )}

        <RemoteTracks
          updateRemoteStats={this.props.updateRemoteStats}
          mutateSetPinnedUser={this.props.mutateSetPinnedUser}
          tracksOfThisParticipant={this.props.tracksOfThisParticipant}
          doesParticipantHasAudio={this.props.doesParticipantHasAudio}
          doesParticipantHasVideo={this.props.doesParticipantHasVideo}
        />
      </div>
    )
  }
}

export default Single
