import React from 'react'
import { inject, observer } from 'mobx-react'
import { X } from 'react-feather'

import { AvatarComponent } from '@components'

@inject(({ stores }) => stores)
@observer
class WaitingRoom extends React.PureComponent {
  render() {
    const { conferenceState } = this.props
    return (
      <div className='wait-room-container'>
        <AvatarComponent
          name={`${conferenceState.conference.createdBy.firstname} ${conferenceState.conference.createdBy.lastname}`}
          size={100}
          isShowTooltip={false}
        />
        <span className='conference-name'>Name: {conferenceState.conference.name}</span>
        <span className='title'>Conference has been locked</span>
        <span className='sub-title'>You can join the meeting after the host admits you</span>

        <button onClick={this.props.leaveConference}><X /></button>
      </div>
    )
  }
}

export default WaitingRoom
