import React from 'react'
import { withRouter } from 'react-router'
import { withTranslation } from 'react-i18next'
import { Checkbox, Button } from 'antd'

import { client, M_SEND_EMAIL } from '@graphql'

import successIcon from '@misc/images/toolbar/success.svg'
import errorIcon from '@misc/images/toolbar/error.svg'

const STATUS = {
  INVITE: 'INVITE',
  SENDING: 'SENDING',
  SENT: {
    SUCCESS: 'SUCCESS',
    ERROR: 'ERROR'
  }
}

@withRouter
class TabRemind extends React.PureComponent {
  state = {
    status: STATUS.INVITE,
    selectedEmail: []
  }

  handleSelectEmail = (email) => {
    this.setState(_preState => ({
      selectedEmail: email
    }))
  }

  handleRemindEmail = () => {
    this.setState(_preState => ({
      ..._preState,
      status: STATUS.SENDING
    }))

    client
      .mutate({
        mutation: M_SEND_EMAIL,
        variables: {
          typeSend: 'REMIND',
          typeEmail: 'DETAIL_CONFERENCE',
          conferenceId: this.props.match.params.ID,
          emails: this.state.selectedEmail
        }
      })
      .then(({ data: { sendEmail } }) => {
        this.setState(_preState => ({
          ..._preState,
          status: STATUS.SENT.SUCCESS
        }))
      })
      .catch(error => {
        this.setState(_preState => ({
          ..._preState,
          status: STATUS.SENT.ERROR
        }))
      })
  }

  handleResetInvite = () => {
    this.setState(_preState => ({
      ..._preState,
      status: STATUS.INVITE,
      selectedEmail: []
    }))
  }

  render() {
    const { i18n } = this.props
    const htmlInviteEmail = this.props.inviteEmail.map((item) => {
      return (<Checkbox key={item._id} value={item.email}>{item.email}</Checkbox>)
    })

    const htmlUserEmail = this.props.userEmail.filter(e => e.user).map((item) => {
      const displayEmail = `${item.user.email} (${item.user.firstname} ${item.user.lastname})`
      return (
        <Checkbox
          key={item.user._id}
          value={item.user.email}
        >
          {displayEmail.length > 35 ? displayEmail.slice(0, 35) + '...' : displayEmail}
        </Checkbox>
      )
    })

    const renderElement = {
      [STATUS.INVITE]: (
        < div className='box-invite' >
          <p>{i18n.t('meeting.titleRemind')}</p>
          <div className='wrapper-checkbox'>
            <Checkbox.Group defaultValue={this.state.selectedEmail} onChange={this.handleSelectEmail}>
              {
                htmlInviteEmail
              }
              {
                htmlUserEmail
              }
            </Checkbox.Group>
          </div>
          <Button
            type='primary'
            disabled={this.state.selectedEmail.length === 0}
            onClick={this.handleRemindEmail}
          >
            {i18n.t('meeting.sendReminder')}
      </Button>
        </div>
      ),
      [STATUS.SENDING]: (
        <div className='box-loading'>
          <div className='spinner'></div>
          <p>{i18n.t('meeting.sendInvite')}</p>
        </div>
      ),
      [STATUS.SENT.SUCCESS]: (
        <div className='box-sent'>
          <img src={successIcon} alt='icon' />
      <span className='success'>{this.state.selectedEmail.length} {i18n.t('meeting.sent')}</span>
          <div className='group-btn'>
      <Button type='primary' onClick={this.handleResetInvite}>{i18n.t('meeting.remindMore')}</Button>
      <Button type='primary' ghost onClick={this.props.handleCancel}>{i18n.t('meeting.done')}</Button>
          </div>
        </div>
      ),
      [STATUS.SENT.ERROR]: (
        <div className='box-sent'>
          <img src={errorIcon} alt='icon' />
      <span className='error'>{this.state.selectedEmail.length} {i18n.t('meeting.notsent')}</span>
          <div className='group-btn'>
            <Button type='primary' onClick={this.handleResetInvite}>{i18n.t('meeting.remindMore')}</Button>
            <Button type='primary' ghost onClick={this.props.handleCancel}>{i18n.t('meeting.done')}</Button>
          </div>
        </div>
      ),
    }

    return (
      <div className='wrapper-remind' >
        {
          renderElement[this.state.status]
        }
      </div>
    )
  }
}

export default withTranslation()(TabRemind)
