import React from 'react'
import { inject, observer } from 'mobx-react'
import { withTranslation } from 'react-i18next'
import { withRouter } from 'react-router-dom'
import { Menu, Dropdown } from 'antd'
import './Participant.less'

import { appConstants, checkPermission } from '@constants'
import { client, M_CONFFERENCE_SET_USER_ROLE, M_KICK_CONFERENCE_USER } from '@graphql'
import { returnError } from '@utils'

import more from '@misc/images/more.svg'
import lecture from '@misc/images/attendee/lecture.svg'
import noSound from '@misc/images/attendee/no-sound.svg'
import note from '@misc/images/attendee/note.svg'
import search from '@misc/images/attendee/search.svg'
import user from '@misc/images/attendee/user.svg'
import signOut from '@misc/images/attendee/sign-out.svg'
import avatar from '@misc/images/no-avatar.svg'

import chat from '@misc/images/attendee/chat.svg'
// import tab from '@misc/images/attendee/multi-tab.svg'
// import bandwidth from '@misc/images/bandwidth.svg'

const { ROLE } = appConstants

@inject(({ stores }) => stores)
@observer
class TabParticipant extends React.PureComponent {
  state = {
    visibleInput: false,
    idTarget: '',
    name: ''
  }

  handleShowInput = () => {
    this.setState(_preState => ({
      visibleInput: true
    }))
  }

  handleHideInput = () => {
    this.setState(_preState => ({
      visibleInput: false
    }))
  }

  handleChangeVisible = (id, name) => {
    this.setState(_prevState => ({
      idTarget: id,
      name
    }))
  }

  handleSetRole = role => {
    client
      .mutate({
        mutation: M_CONFFERENCE_SET_USER_ROLE,
        variables: {
          conferenceId: this.props.conferenceState.conference._id,
          userId: this.state.idTarget,
          roleId: role
        }
      })
      .catch(error =>
        returnError('conferenceSetUserRole', {
          error,
          errorMessage: 'Err! Please try again later!'
        })
      )
  }

  mutationKickConferenceUser = () => {
    client
      .mutate({
        mutation: M_KICK_CONFERENCE_USER,
        variables: {
          conferenceId: this.props.conferenceState.conference._id,
          userId: this.state.idTarget
        }
      })
      .catch(error =>
        returnError('kickConferenceUser', {
          error,
          errorMessage: 'Err! Please try again later!'
        })
      )
  }

  render() {
    const {
      state: { visibleInput },
      props: { i18n, conferenceState, jitsiStore },
      handleHideInput
    } = this

    const isOnline = userId =>
      jitsiStore.participants.some(({ displayName }) => displayName && displayName.indexOf(userId) > -1) ||
      conferenceState.myUserId === userId

    const filterActiveUserByRole = specifiedRole =>
      this.props.conferenceState.conferenceUsers.filter(
        ({ user, role }) => role && role._id === specifiedRole && user && isOnline(user._id)
      )

    const inactive = conferenceState.conferenceUsers.filter(({ user }) => user && !isOnline(user._id))

    const sections = [
      { key: 'owner', data: filterActiveUserByRole(ROLE.OWNER) },
      { key: 'moderator', data: filterActiveUserByRole(ROLE.MODERATOR) },
      { key: 'participant', data: filterActiveUserByRole(ROLE.PARTICIPANT) }
    ]

    const headerMenu = (
      <Menu className='custom-menu'>
        <Menu.Item onClick={this.handleShowInput}>
          <span>
            <img src={search} alt='icon' />
            {i18n.t('findAttendee')}
          </span>
        </Menu.Item>
        <Menu.Item>
          <span>
            <img src={noSound} alt='icon' />
            {i18n.t('offAllVoice')}
          </span>
        </Menu.Item>
      </Menu>
    )

    const itemMenu = (
      <Menu className='custom-menu'>
        {checkPermission(conferenceState.myPermissions, 'CHAT') ? (
          <Menu.Item
            onClick={() =>
              this.props.handleOpenChat({
                _id: this.state.idTarget,
                name: this.state.name
              })
            }
          >
            <span>
              <img src={chat} alt='icon' />
              Trò chuyện
            </span>
          </Menu.Item>
        ) : null}
        {checkPermission(conferenceState.myPermissions, 'AUTHORIZE') ? (
          <Menu.Item onClick={() => this.handleSetRole('OWNER')}>
            <span>
              <img src={user} alt='icon' />
              {i18n.t('roleOwner')}
            </span>
          </Menu.Item>
        ) : null}

        {checkPermission(conferenceState.myPermissions, 'AUTHORIZE') ? (
          <Menu.Item onClick={() => this.handleSetRole('PARTICIPANT')}>
            <span>
              <img src={lecture} alt='icon' />
              {i18n.t('roleParticipants')}
            </span>
          </Menu.Item>
        ) : null}

        {checkPermission(conferenceState.myPermissions, 'AUTHORIZE') ? (
          <Menu.Item onClick={() => this.handleSetRole('MODERATOR')}>
            <span>
              <img src={note} alt='icon' />
              {i18n.t('roleSupervisor')}
            </span>
          </Menu.Item>
        ) : null}

        {checkPermission(conferenceState.myPermissions, 'KICK_PARTICIPANT') ? (
          <Menu.Item onClick={this.mutationKickConferenceUser}>
            <span>
              <img src={signOut} alt='icon' />
              {i18n.t('deleteSession')}
            </span>
          </Menu.Item>
        ) : null}
      </Menu>
    )

    return (
      <div className='attendee'>
        <div className='__head'>
          <div className='_title'>
            <p>{`${conferenceState.conferenceUsers.filter(({ user }) => user && isOnline(user._id)).length} ${i18n.t(
              'attendee'
            )}`}</p>
            <Dropdown overlay={headerMenu} trigger={['click']}>
              <img src={more} alt='icon' style={{ cursor: 'pointer' }} />
            </Dropdown>
          </div>
          {visibleInput ? (
            <input type='text' placeholder='Tìm người tham dự' onBlur={handleHideInput} autoFocus />
          ) : (
              <React.Fragment />
            )}
        </div>

        <div className='__body'>
          {sections.map(({ key, data }) => {
            return (
              <div className='_item' key={key}>
                <div className='item__head'>
                  <p>
                    {i18n.t(key)} ({data.length})
                  </p>
                </div>
                <div className='item__body'>
                  {data.map(({ user }, i) => {
                    return (
                      <div className='user-item' key={i}>
                        <img src={avatar} alt='icon' className='user-avatar' />
                        <p className={key}>{`${user.firstname} ${user.lastname}`}</p>
                        {user._id !== this.props.conferenceState.myInfo._id ? (
                          <Dropdown
                            overlay={itemMenu}
                            trigger={['click']}
                            onClick={() => this.handleChangeVisible(user._id, `${user.firstname} ${user.lastname}`)}
                          >
                            <img src={more} alt='icon' style={{ cursor: 'pointer' }} />
                          </Dropdown>
                        ) : null}
                      </div>
                    )
                  })}
                </div>
              </div>
            )
          })}

          {/* Inactive */}
          <div className='disconnect-item' key={3}>
            <div className='disconnect-item__head'>
              <p>Không hoạt động</p>
            </div>
            <div className='disconnect-item__body'>
              {inactive.map(({ user }, i) => {
                return (
                  <div className='disconnect-user-item' key={i}>
                    <img src={signOut} alt='icon' className='user-avatar' />
                    <p className='disconnect-user-text'>Đã rời đi:</p>
                    <p className='disconnect-user-name'>{`${user.firstname} ${user.lastname}`}</p>
                  </div>
                )
              })}
            </div>
          </div>
        </div>
      </div>
    )
  }
}

