import React from 'react'
import { Modal } from 'antd'
import './index.less'

class ModalNotification extends React.PureComponent {
  render() {
    return (
      <Modal
        className='modal-notification'
        title="Email không hợp lệ"
        visible={this.props.visible}
        centered
        onCancel={this.props.handleCancel}
      >
        <p>Xin chào. Email của bạn không hợp lệ với hệ thống. Vui lòng chờ Admin xác minh email</p>
        <p>Danh sách Email hợp lệ:</p>
        <div className='wrapper-email'>
          {
            this.props.emailSettings.filter(e => e.isActive).map((item, index) => {
              return (
                <div className='email-item' key={index}>
                  <p className='title'>@</p>
                  <p className='content'>{item.rootEmail}</p>
                </div>
              )
            })
          }
        </div>
      </Modal>
    )
  }
}

export default ModalNotification
