import React from 'react'
import { inject, observer } from 'mobx-react'
import { Document, Page, pdfjs } from 'react-pdf'
import { Carousel, Icon, Dropdown, Menu } from 'antd'

import Cursur from '@misc/images/ngocly/cursor.svg'
import Undo from '@misc/images/ngocly/undo.svg'
import Rectangle from '@misc/images/ngocly/rectangle.svg'
import Circle from '@misc/images/ngocly/circle.svg'
import Pencil from '@misc/images/ngocly/pencil.svg'
import Arrow from '@misc/images/ngocly/arrow.svg'
import BGColor from '@misc/images/ngocly/paint.svg'
import Clear from '@misc/images/ngocly/clear.svg'
import Brush from '@misc/images/ngocly/brush.svg'
import { _SLIDE, _drawType } from '@constants'
import client, { M_SLIDE_SHOW, M_DRAW_SCREEN } from '@graphql'
import ModalContentResource from '@pages/conference2/modals/ModalContentResource'
import Canvas from '../components/Canvas'

pdfjs.GlobalWorkerOptions.workerSrc = `//cdnjs.cloudflare.com/ajax/libs/pdf.js/${pdfjs.version}/pdf.worker.js`

const listColor = [
  'F44235',
  'EA1E63',
  '9C27B1',
  '673AB6',
  '3E50B5',
  '2196F3',
  '02A9F5',
  '00BCD5',
  '009788',
  '4BB050',
  '8BC24A',
  'CDDC39',
  'FEEB3C',
  'FEC107',
  'FF9700',
  'FE5722',
  '785546',
  '9E9E9E',
  '697D8A'
]

const listHeight = [1, 2, 3, 4, 5]

@inject(({ stores }) => stores)
@observer
class ShareResourceScreen extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      pageNumber: 1,
      numPages: 0,
      rotate: 0
    }
  }

  _renderResource = imageURL => {
    return <img key={imageURL} id={imageURL} src={imageURL} alt='img' />
  }

  _visibleRotate = () => {
    const {
      conferenceState: { fileOpen, activeDraw }
    } = this.props

    if (!fileOpen) return false
    if (fileOpen.isSlide) return false
    if (fileOpen.isImage && activeDraw) return false
    return true
  }

  // Slide Share
  _canNext = () => this.props.conferenceState.positionSlide < this.props.conferenceState.fileOpen.resources.length - 1

  _canPrevious = () => this.props.conferenceState.positionSlide > 0

  _handleControl = type => {
    if (type === _SLIDE._actionSlide.NEXT) {
      // this.carousel.slick.slickNext()
      this._mutateSlide(type)
    }

    if (type === _SLIDE._actionSlide.PREVIOUS) {
      // this.carousel.slick.slickPrev()
      this._mutateSlide(type)
    }
  }

  _mutateSlide = type => {
    client.mutate({
      mutation: M_SLIDE_SHOW,
      variables: {
        _id: this.props.conferenceState.fileOpen._id,
        type
      }
    })
  }

  //PDF Share
  _onDocumentLoadSuccess = ({ numPages }) => {
    this.setState({ numPages, pageNumber: 1 })
  }

  _nextPage = () => this.setState(prev => ({ pageNumber: prev.pageNumber + 1 }))
  _prevPage = () => this.setState(prev => ({ pageNumber: prev.pageNumber - 1 }))

  // Image Share
  _getCurrentRotationElm = elm => {
    let st = window.getComputedStyle(elm, null)
    let tm =
      st.getPropertyValue('-webkit-transform') ||
      st.getPropertyValue('-moz-transform') ||
      st.getPropertyValue('-ms-transform') ||
      st.getPropertyValue('-o-transform') ||
      st.getPropertyValue('transform') ||
      'none'

    if (tm !== 'none') {
      let values = tm
        .split('(')[1]
        .split(')')[0]
        .split(',')

      let angle = Math.round(Math.atan2(values[1], values[0]) * (180 / Math.PI))
      return angle < 0 ? angle + 360 : angle
    }
    return 0
  }

  _rotateLeft = type => {
    if (type === 'image') {
      let img = document.getElementById(this.props.conferenceState.fileOpen.resources[0])
      let canvasDraw = document.getElementsByClassName('konvajs-content')
      let rotate = this._getCurrentRotationElm(img) - 90
      img.style = `transform: rotate(${rotate < 0 ? rotate + 360 : rotate}deg)`
      canvasDraw[0].style.transform = `rotate(${rotate < 0 ? rotate + 360 : rotate}deg)`
    }

    if (type === 'pdf') {
      let pdf = document.getElementsByClassName('react-pdf__Page__canvas')
      let rotate = this._getCurrentRotationElm(pdf[0]) - 90
      pdf[0].style = `transform: rotate(${rotate < 0 ? rotate + 360 : rotate}deg)`
    }
  }

  _rotateRight = type => {
    if (type === 'image') {
      let img = document.getElementById(this.props.conferenceState.fileOpen.resources[0])
      let canvasDraw = document.getElementsByClassName('konvajs-content')
      let rotate = this._getCurrentRotationElm(img) + 90
      img.style = `transform: rotate(${rotate > 270 ? rotate - 360 : rotate}deg)`
      canvasDraw[0].style.transform = `rotate(${rotate > 270 ? rotate - 360 : rotate}deg)`
    }

    if (type === 'pdf') {
      let pdf = document.getElementsByClassName('react-pdf__Page__canvas')
      let rotate = this._getCurrentRotationElm(pdf[0]) + 90
      pdf[0].style = `transform: rotate(${rotate > 270 ? rotate - 360 : rotate}deg)`
    }
  }

  _changePropLine = (height, color) => {
    this.props.conferenceState.setState({ heightLine: height, colorLine: color })

    let colorElm = document.getElementById('annotation-line-color')

    if (colorElm) {
      colorElm.style.backgroundColor = color
    }
  }

  _clearDraw = () => {
    this._sendDraw([])
  }

  _undoDraw = () => {
    const { drawables } = this.props.conferenceState
    if (drawables.length > 0) {
      drawables.pop()
      this._sendDraw(drawables.map(draw => JSON.stringify(draw)))
    }
  }

  _sendDraw = line => {
    const {
      conferenceState: { fileOpen, myUserId }
    } = this.props
    if (fileOpen && fileOpen.isImage && fileOpen.uploadedBy._id === myUserId) {
      client
        .mutate({
          mutation: M_DRAW_SCREEN,
          variables: {
            conferenceId: this.props.conferenceState.conference._id,
            line,
            imageUrl: this.props.conferenceState.fileOpen.resources[0]
          }
        })
        .then(() => {})
        .catch(err => console.log(err))
    }
  }

  //END

  componentDidMount() {
    const { fileOpen, positionSlide, setState } = this.props.conferenceState
    if (fileOpen && fileOpen.isSlide) {
      setState({ carousel: this.carousel })
      this.carousel.slick.slickGoTo(positionSlide)
    }
  }

  componentDidUpdate() {
    const { fileOpen, positionSlide, carousel, setState } = this.props.conferenceState
    if (!carousel && fileOpen && fileOpen.isSlide) {
      setState({ carousel: this.carousel })
      this.carousel.slick.slickGoTo(positionSlide)
    }

    if (!fileOpen || !fileOpen.isSlide) {
      setState({ carousel: null })
    }

    if (fileOpen && fileOpen.isImage) {
      let img = document.getElementById(fileOpen.resources[0])
      setState({
        widthImage: img.clientWidth,
        heightImage: img.clientHeight
      })
    }
  }

  render() {
    const {
      conferenceState: {
        fileOpen,
        myUserId,
        widthImage,
        activeDraw,
        heightLine,
        colorLine,
        newDrawableType,
        drawables,
        setState
      }
    } = this.props

    return (
      <div className='resources-container'>
        <div className='button-top'>
          {this._visibleRotate() ? (
            <div className='button-undo' onClick={() => this._rotateLeft(fileOpen.isImage ? 'image' : 'pdf')}>
              <Icon type='undo' />
            </div>
          ) : (
            <React.Fragment />
          )}
          <button onClick={() => this.openModal()}>
            <div>{fileOpen && fileOpen.filename ? fileOpen.filename : `Chia s tp tin`}</div>
            <Icon type='down' />
          </button>

          {fileOpen && fileOpen.isImage && fileOpen.uploadedBy._id === myUserId && (
            <div
              className={`button-draw ${activeDraw ? 'active' : ''}`}
              onClick={() => setState({ activeDraw: !activeDraw })}
            >
              <img alt='draw' src={Brush} width='15px' />
            </div>
          )}

          {this._visibleRotate() ? (
            <div className='button-redo' onClick={() => this._rotateRight(fileOpen.isImage ? 'image' : 'pdf')}>
              <Icon type='redo' />
            </div>
          ) : (
            <React.Fragment />
          )}
        </div>

        {activeDraw && (
          <div className='toolbar-annotation'>
            <div className='toolbar-container'>
              <Dropdown
                trigger={['click']}
                overlay={
                  <Menu onClick={e => e.domEvent.preventDefault()} className='menu-color-picker'>
                    {listColor.map(item => (
                      <Menu.Item
                        key={item}
                        id={`color-${item}`}
                        onClick={() => this._changePropLine(heightLine, `#${item}`)}
                      >
                        {colorLine === `#${item}` && <Icon type='check' />}
                      </Menu.Item>
                    ))}
                  </Menu>
                }
              >
                <div id='annotation-line-color' style={{ backgroundColor: colorLine }}>
                  {/* <Icon type='bg-colors' /> */}
                  <img src={BGColor} alt='brushcolor' width='15px' />
                </div>
              </Dropdown>

              <Dropdown
                trigger={['click']}
                overlay={
                  <Menu className='menu-line-heignt-picker'>
                    {listHeight.map(item => (
                      <Menu.Item key={item} onClick={() => this._changePropLine(item, colorLine)}>
                        <div>{item}px</div>
                        <svg style={{ height: '5px', width: '70px' }}>
                          <line x1='0' y1='0' x2='70' y2='0' stroke={colorLine} strokeWidth={item * 3} />
                        </svg>
                      </Menu.Item>
                    ))}
                  </Menu>
                }
              >
                <div>
                  <p id='annotation-line-width'>{heightLine}px</p>
                </div>
              </Dropdown>

              <div onClick={this._undoDraw} style={{ backgroundColor: drawables.length > 0 ? '#5d9c5a' : '#fff' }}>
                <img src={Undo} alt='eraser' width='15px' />
              </div>

              <div onClick={() => this._sendDraw([])}>
                <img src={Clear} alt='eraser' width='15px' />
              </div>

              <div
                onClick={() => setState({ newDrawableType: _drawType.ARROW })}
                style={{ backgroundColor: newDrawableType === _drawType.ARROW ? '#5d9c5a' : '#fff' }}
              >
                <img src={Arrow} alt='eraser' width='15px' />
              </div>

              <div
                onClick={() => setState({ newDrawableType: _drawType.CIRCLE })}
                style={{ backgroundColor: newDrawableType === _drawType.CIRCLE ? '#5d9c5a' : '#fff' }}
              >
                <img src={Circle} alt='eraser' width='15px' />
              </div>

              <div
                onClick={() => setState({ newDrawableType: _drawType.RECT })}
                style={{ backgroundColor: newDrawableType === _drawType.RECT ? '#5d9c5a' : '#fff' }}
              >
                <img src={Rectangle} alt='eraser' width='15px' />
              </div>

              <div
                onClick={() => setState({ newDrawableType: _drawType.FREEPATH })}
                style={{ backgroundColor: newDrawableType === _drawType.FREEPATH ? '#5d9c5a' : '#fff' }}
              >
                <img src={Pencil} alt='eraser' width='15px' />
              </div>

              <div
                onClick={() => setState({ newDrawableType: _drawType.CURSOR })}
                style={{ backgroundColor: newDrawableType === _drawType.CURSOR ? '#5d9c5a' : '#fff' }}
              >
                <img src={Cursur} alt='eraser' width='15px' />
              </div>
            </div>
          </div>
        )}

        <ModalContentResource sendDraw={this._sendDraw} openModal={cb => (this.openModal = cb)} />

        {fileOpen ? (
          fileOpen.isImage ? (
            <div className='image-share-container'>
              {this._renderResource(fileOpen.resources[0])}
              {widthImage && <Canvas sendDraw={this._sendDraw} />}
            </div>
          ) : fileOpen.isSlide ? (
            <div className='slide-share-container'>
              <Carousel ref={cb => (this.carousel = cb)}>
                {fileOpen.resources.map(imageURL => {
                  return this._renderResource(imageURL)
                })}
              </Carousel>
              {fileOpen.uploadedBy._id === myUserId && this._canPrevious() && (
                <div className='btn-prev' onClick={() => this._handleControl(_SLIDE._actionSlide.PREVIOUS)}>
                  <Icon type='left' />
                </div>
              )}

              {fileOpen.uploadedBy._id === myUserId && this._canNext() && (
                <div className='btn-next' onClick={() => this._handleControl(_SLIDE._actionSlide.NEXT)}>
                  <Icon type='right' />
                </div>
              )}

              {/* <div className='current-page'>
                <h2>{position}</h2>
              </div> */}
            </div>
          ) : fileOpen.isPDF ? (
            <Document file={fileOpen.resources[0]} onLoadSuccess={this._onDocumentLoadSuccess}>
              <Page pageNumber={this.state.pageNumber} />
              {this.state.pageNumber > 1 && (
                <div className='btn-prev' onClick={this._prevPage}>
                  <Icon type='left' />
                </div>
              )}
              {this.state.pageNumber - this.state.numPages < 0 && (
                <div className='btn-next' onClick={this._nextPage}>
                  <Icon type='right' />
                </div>
              )}
              <div className='num-pages'>
                Page {this.state.pageNumber} of {this.state.numPages}
              </div>
            </Document>
          ) : (
            <React.Fragment />
          )
        ) : (
          <React.Fragment />
        )}
      </div>
    )
  }
}

export default ShareResourceScreen
