import React, { Component } from 'react'
import { Modal, Button, Checkbox, Form, Input } from 'antd'

import { client, M_EMAIL_PANELISTS } from '@graphql'
import { returnError, newNotification } from '@utils'

let id = 0
class ChangePanelistsModal extends Component {
  state = {
    immediatelySend: false,
    tempPanelist: []
  }
  handleOk = () => {
    const { form } = this.props

    form.validateFields((err, values) => {
      if (!err) {
        let tempValues =
          values && values.emails
            ? values.emails.map((e, i) => ({
              name: values.names[i],
              email: e
            }))
            : []

        this.props.handleChangePanelists(tempValues)

        // MUTATION EMAIL PANELIST
        client
          .mutate({
            mutation: M_EMAIL_PANELISTS,
            variables: {
              webinarId: this.props.webinarId,
              emailInput: values.emails
            }
          })
          .then(({ data: { emailPanelists } }) => {
            newNotification('Send email success', 'success', { container: 'top-right' })
          })
          .catch(error => returnError(
            'Get Link',
            {
              error,
              errorMessage: 'Cannot send email'
            },
            { container: 'top-right' }
          ))
      }
    })
  }
  remove = k => {
    const { form } = this.props
    // can use data-binding to get
    const keys = form.getFieldValue('keys')
    // We need at least one passenger
    // can use data-binding to set
    form.setFieldsValue({
      keys: keys.filter(key => key !== k)
    })
  }

  add = () => {
    const { form } = this.props
    form.validateFields((err, values) => {
      if (err) {
        console.log(err)
      } else {
        const keys = form.getFieldValue('keys')
        const nextKeys = keys.concat(id++)
        // can use data-binding to set
        // important! notify form to detect changes
        form.setFieldsValue({
          keys: nextKeys
        })
      }
    })
    // can use data-binding to get
  }
  render() {
    const {
      showChangePanelistsModal,
      closeChangePanelistsModal,
      form: { getFieldDecorator, getFieldValue }
    } = this.props

    getFieldDecorator('keys', { initialValue: [] })

    const keys = getFieldValue('keys')

    const formItems = keys.map((k, index) => (
      <div className='formItemHolder' key={k}>
        <Form.Item required={false}>
          {getFieldDecorator(`names[${k}]`, {
            rules: [
              {
                required: true,
                whitespace: true,
                message: 'Name.'
              }
            ]
          })(<Input placeholder='Name' style={{ marginRight: 8 }} />)}
        </Form.Item>
        <Form.Item required={false}>
          {getFieldDecorator(`emails[${k}]`, {
            rules: [
              {
                required: true,
                whitespace: true,
                message: 'Email Address.'
              },
              {
                pattern: /^([A-Z|a-z|0-9](\.|_){0,1})+[A-Z|a-z|0-9]@([A-Z|a-z|0-9])+((\.){0,1}[A-Z|a-z|0-9]){2}\.[a-z]{2,3}$/gm,
                message: 'Not Valid Email Address.'
              }
            ]
          })(<Input placeholder='Email Address' style={{ marginRight: 8 }} />)}
        </Form.Item>
        <div>
          <Button size='small'>Send</Button>
          <Button size='small'>Copy</Button>
          <Button size='small' onClick={() => this.remove(k)} type='danger'>
            Delete
          </Button>
        </div>
        {/* <Icon
          className='dynamic-delete-button'
          type='minus-circle-o'
          onClick={() => this.remove(k)}
        /> */}
      </div>
    ))
    return (
      <Modal
        width='50vw'
        className='changePanelistsModal'
        title='Panelists'
        visible={showChangePanelistsModal}
        onOk={this.handleOk}
        onCancel={closeChangePanelistsModal}
        footer={
          <div style={{ display: 'flex', justifyContent: 'flex-end' }}>
            <Checkbox
              style={{ margin: 'auto auto auto 0' }}
              onChange={e =>
                this.setState({ immediatelySend: e.target.checked })
              }
            >
              Send invitation to all newly added panelists immediately
            </Checkbox>
            <Button key='submit' type='primary' onClick={this.handleOk}>
              Save
            </Button>
            <Button key='back' onClick={closeChangePanelistsModal}>
              Cancel
            </Button>
          </div>
        }
      >
        <p>Invite a person or a Zoom room as a webinar panelist</p>

        <Form>
          {formItems}
          <Form.Item>
            <span onClick={this.add} className='clickableSpan'>
              Add Another Panelist
            </span>
          </Form.Item>
        </Form>
      </Modal>
    )
  }
}
const ChangePanelistsModalWrapped = Form.create({ name: 'dynamic_form_item' })(
  ChangePanelistsModal
)
export default ChangePanelistsModalWrapped
