import React from 'react'
import { withTranslation } from 'react-i18next'
import { Form, Input, Row, Col, InputNumber } from 'antd'

class FormTimer extends React.Component {
  handleSubmit = e => {
    e.preventDefault()
    this.props.form.validateFields((err, values) => {
      if (!err) {
        console.log('Received values of form: ', values)
      }
    })
  }

  render() {
    const {
      props: {
        form: {
          getFieldDecorator
        },
        i18n
      }
    } = this
    return (
      <Form onSubmit={this.handleSubmit} className='login-form' ref='form'>
        <Row gutter={8}>
          <Col span={12}>
            <Form.Item label={i18n.t('minute')}>
              {getFieldDecorator('minute', {
                initialValue: 1,
                rules: [
                  {
                    type: 'number',
                    min: 1,
                    max: 30,
                    required: true,
                  }
                ]
              })(<InputNumber min={1} max={30} style={{ width: '100%' }} />)}
            </Form.Item>
          </Col>
          <Col span={12}>
            <Form.Item label={i18n.t('seconds')}>
              {getFieldDecorator('seconds', {
                initialValue: 0,
                rules: [
                  {
                    type: 'number',
                    min: 0,
                    max: 59
                  }
                ]
              })(<InputNumber min={0} max={59} style={{ width: '100%' }} />)}
            </Form.Item>
          </Col>
        </Row>
        <Form.Item label={i18n.t('timerDescription')}>
          {getFieldDecorator('description')(<Input />)}
        </Form.Item>
        <div className='char-text-container'>Còn lại 120 ký tự</div>
      </Form>
    )
  }
}

export default Form.create({ name: 'normal_login' })(withTranslation()(FormTimer))
