import React from 'react'
import { withTranslation } from 'react-i18next'
import { inject, observer } from 'mobx-react'
import { Row, Col, Button, Icon, Modal, Select, Breadcrumb, Collapse } from 'antd'
import moment from 'moment'

import client, { M_DELETE_CONFERENCE, M_DELETE_EVENT_CALENDAR } from '@graphql'
import { returnError, newNotification, isSupperAdmin } from '@utils'

import GetLink from './GetLink'
import Members from '../components/ButtonMore'
import ModalDelete from '../components/ModalDelete'
import ModalInvite from '../components/ModalInvite'
import googleCalendarAPI from '../../../calendar/GoogleCalendarAPI'
import ConferenceStatistic from './ConferenceStatistic'

import googleCalendar from '@misc/images/google-calendar.svg'
import chervonRightIcon from '@misc/images/chervon-right.svg'
import { withRouter, Link } from 'react-router-dom'
import { ROLE } from '@constants'

@withRouter
@inject(({ stores }) => stores)
@observer
class InfoConference extends React.PureComponent {
  constructor(props) {
    super(props)
    this.state = {
      listInvite: [],
      visible: false,
      statusEvent: ''
    }
  }

  _handleDeleteConference = _id => {
    client
      .mutate({
        mutation: M_DELETE_CONFERENCE,
        variables: { conferenceId: _id }
      })
      .then(({ data: { deleteConference } }) => {
        if (deleteConference) {
          this.props.history.push('/')
          return newNotification(this.props.i18n.t('notification.deleteMeetingSuccess'), 'success')
        }
      })
      .catch(error =>
        returnError(
          'Delete Conference',
          {
            error,
            errorMessage: this.props.i18n.t('notification.deleteMeetingError')
          },
          { container: 'top-right' }
        )
      )
  }

  _renderChildren = () => {
    const { users, conference } = this.props
    const newUser = users.filter(item =>
      conference.users ? conference.users.findIndex(x => x.user._id === item._id) === -1 : false
    )
    return newUser.map(item => {
      return <Select.Option key={item._id}>{`${item.firstname} ${item.lastname}`}</Select.Option>
    })
  }

  _showModal = () => {
    this.setState({ visible: true, listInvite: [] })
  }
  _handleCancel = e => {
    this.setState({ visible: false, listInvite: [] })
  }

  _handleChange = value => {
    this.setState({ listInvite: value })
  }

  checkEvent = async () => {
    const { conference, globalState } = this.props
    if (
      conference.calendar &&
      conference.createdBy &&
      globalState.myInfo &&
      conference.createdBy._id === globalState.myInfo._id
    ) {
      const request = await googleCalendarAPI.getEvent(conference.calendar._id.eventId)

      if (request) {
        if (request.result.status === 'cancelled') {
          client.mutate({
            mutation: M_DELETE_EVENT_CALENDAR,
            variables: {
              conferenceId: conference._id,
              eventId: conference.calendar._id.eventId
            }
          })
        }

        if (this.state.statusEvent !== request.result.status)
          this.setState(_preState => ({
            ..._preState,
            statusEvent: request.result.status
          }))
      }
    }
  }

  handleSaveEvent = async () => {
    googleCalendarAPI.insertEvent(this.props.conference, this.props._queryConferenceDetail)
  }

  componentDidUpdate() {
    // Google API
    if (this.props.globalState.isGoogleAPI) {
      this.checkEvent()
    }
  }

  render() {
    const { conference, globalState, i18n, handleJoinConference } = this.props

    const initHour = Math.floor(conference.duration / 3600000)
    const initMinute = Math.floor((conference.duration - initHour * 3600000) / 60000)

    const profiles = globalState.myInfo?.profiles
    const isOwner =
      globalState?.myInfo?._id && conference?.createdBy?._id && conference.createdBy._id === globalState.myInfo._id
    //TODO: Upgrade check by Permission code
    const isAuthen =
      isOwner || isSupperAdmin(profiles) || profiles?.some(profile => profile.role?.code === ROLE.R_ADMIN)

    return (
      <div className='detail-template'>
        <Breadcrumb separator={<img src={chervonRightIcon} alt='icon' />} className='breadcrumb-template'>
          <Breadcrumb.Item>
            <Link to='/'>{i18n.t('meeting.myMeetings')}</Link>
          </Breadcrumb.Item>
          <Breadcrumb.Item>{conference.name}</Breadcrumb.Item>
        </Breadcrumb>
        <Row className='titleRow'>
          <Col span={6} className='title'>
            <span>{i18n.t('meeting.topic')}:</span>
          </Col>
          <Col span={12}>
            <span>{conference.name}</span>
          </Col>
          <Col span={6} style={{ display: 'flex', justifyContent: 'flex-end' }}>
            <Button
              type='primary'
              className='btn-start mainBtn'
              disabled={Date.now() < conference.startAt || Date.now() >= conference.startAt + conference.duration}
              onClick={() => handleJoinConference(conference.code)}
            >
              {i18n.t('meeting.startMeeting')}
            </Button>
          </Col>
        </Row>

        <Row>
          <Col span={6} className='title'>
            {i18n.t('meeting.time')}:
          </Col>
          <Col span={18}>
            <div>
              <p>{conference.startAt ? moment(conference.startAt).format('MMM Do YYYY, hh:mm a') : null}</p>

              {/* Google API - Calendar */}
              {isOwner && this.props.globalState.isGoogleAPI && (
                this.state.statusEvent === 'cancelled' ? (
                  <div style={{ display: 'flex', alignItems: 'center' }}>
                    <span style={{ marginRight: '10px' }}>{i18n.t('addto')}</span>
                    <button type='button' className='btn-insert-calendar' onClick={this.handleSaveEvent}>
                      <img src={googleCalendar} alt='icon' />
                      <span>Google Calendar</span>
                    </button>
                  </div>
                ) : !conference.calendar ? (
                  <div style={{ display: 'flex', alignItems: 'center' }}>
                    <span style={{ marginRight: '10px' }}>{i18n.t('meeting.addto')}</span>
                    <button type='button' className='btn-insert-calendar' onClick={this.handleSaveEvent}>
                      <img src={googleCalendar} alt='icon' />
                      <span>Google Calendar</span>
                    </button>
                  </div>
                ) : (
                      <div style={{ display: 'flex', alignItems: 'center' }}>
                        <span style={{ marginRight: '10px' }}>{i18n.t('meeting.openCalendar')}: </span>
                        <a
                          style={{ textDecoration: 'underline' }}
                          href={conference.calendar.htmlLink}
                          target='_blank'
                          rel='noopener noreferrer'
                        >
                          {i18n.t('meeting.clickHere')}
                        </a>
                      </div>
                    )
              )}
              {/* Google API - Calendar */}

            </div>
          </Col>
        </Row>

        <Row>
          <Col span={6} className='title'>
            {i18n.t('meeting.duration')}:
          </Col>
          <Col span={18}>
            {initHour} {initHour === 1 ? 'hour' : 'hours'}
            <span style={{ display: initMinute === 0 ? 'none' : 'block' }}>
              {initMinute} {initMinute === 1 ? 'minute' : 'minutes'}
            </span>
          </Col>
        </Row>

        <Row>
          <Col span={6} className='title'>
            Meeting Code:
          </Col>
          <Col span={18}>{conference.code}</Col>
        </Row>

        <Row>
          <Col span={6} className='title'>
            {i18n.t('meeting.meetingPassword')}:
          </Col>
          <Col span={18}>
            <span style={{ fontSize: '10px' }}>
              {conference.password ? (
                <Icon style={{ color: '#46C510' }} type='check' />
              ) : (
                  <Icon style={{ color: '#616161' }} type='close' />
                )}
            </span>
            <span style={{ marginRight: '3rem', marginLeft: '0.5rem' }}>{i18n.t('meeting.meetingPassword')}</span>
          </Col>
        </Row>

        <Row>
          <Col span={6} className='title'>
            Video:
          </Col>
          <Col span={18}>
            <Row style={{ borderBottom: 'none', padding: 0 }}>
              <Col span={8}>
                <div className='videoTitle'>
                  <span>{i18n.t('meeting.host')}</span>
                  <span>{i18n.t('meeting.participant')}</span>
                </div>
              </Col>

              <Col span={16}>
                <div className='videoTitle'>
                  <span>{conference.videoHostDefault ? 'On' : 'Off'}</span>
                  <span>{conference.videoParticipantDefault ? 'On' : 'Off'}</span>
                </div>
              </Col>
            </Row>
          </Col>
        </Row>

        {isAuthen && (
          <Row>
            <Col span={6} className='title'>
              {i18n.t('meeting.inviteMembers')}:
            </Col>
            <Col span={18}>
              {/* <span className='clickableSpan' onClick={this._showModal}>
                Open
              </span> */}
              <ModalInvite
                conference={this.props.conference}
                statusEvent={this.state.statusEvent}
                _queryConferenceDetail={this.props._queryConferenceDetail}
              />
            </Col>
          </Row>
        )}

        {isAuthen && (
          <Row>
            <Col span={6} className='title'>
              {i18n.t('meeting.getLink')}:
            </Col>
            <Col span={18}>
              <GetLink
                conferenceId={conference._id}
                conferenceCode={conference.code}
                conferenceUser={conference.users}
              />
            </Col>
          </Row>
        )}

        <Row>
          <Col span={6} className='title'>
            {i18n.t('meeting.members')}:
          </Col>
          <Col span={18}>
            <Members conferenceUser={conference.users} />
          </Col>
        </Row>

        <Row>
          <Col span={6} className='title'>
            {i18n.t('meeting.createBy')}:
          </Col>
          <Col span={18}>
            {conference.createdBy ? `${conference.createdBy.firstname} ${conference.createdBy.lastname}` : ''}
          </Col>
        </Row>
        {/* WORKING HERE */}
        <Row>
          <Col span={6} className='title'>
            {i18n.t('meeting.statistic')}:
          </Col>
          <Col span={18}>
            <Collapse className='detailCollapse'>
              <Collapse.Panel header={i18n.t('meeting.viewDetail')}>
                <ConferenceStatistic conferenceId={conference._id} conferenceUsers={conference.users} />
              </Collapse.Panel>
            </Collapse>
          </Col>
        </Row>

        <Row className='btnRow'>
          <Col span={6}>
            {isAuthen && (
              <span>
                <Icon type='delete' style={{ color: '#5D9C5A', marginRight: 12 }} />
                <ModalDelete
                  type='DETAIL'
                  itemToDelete={this.props.conference}
                  handleDeleteItem={this._handleDeleteConference}
                  title={i18n.t('meeting.deleteMeeting')}
                  textButtonDelete={i18n.t('meeting.deleteMeeting')}
                />
              </span>
            )}
          </Col>
          <Col span={18}>
            {isAuthen && (
              <Button type='primary' ghost className='mainBtn' onClick={this.props.onEditFunction}>
                {i18n.t('meeting.editMeeting')}
              </Button>
            )}
            <Button
              type='primary'
              style={{
                borderRadius: '6px',
                float: 'right',
                height: '30px',
                marginRight: 10,
                fontWeight: '600'
              }}
              disabled={Date.now() < conference.startAt || Date.now() >= conference.startAt + conference.duration}
              onClick={() => handleJoinConference(conference.code)}
            >
              {i18n.t('meeting.startMeeting')}
            </Button>
          </Col>
        </Row>

        <Modal
          className='common-modal'
          title={i18n.t('inviteMembers')}
          visible={this.state.visible}
          onOk={this._handleOk}
          onCancel={this._handleCancel}
          okText={i18n.t('common.add')}
          cancelText={i18n.t('common.cancel')}
        >
          <p>{i18n.t('meeting.inviteUser')}</p>
          <Select
            className='user-conference-select'
            mode='multiple'
            style={{ width: '100%' }}
            placeholder={i18n.t('pleaseSelect')}
            value={this.state.listInvite}
            onChange={this._handleChange}
          >
            {this._renderChildren()}
          </Select>
          <div className='wrapper-button'>
            <Button type='primary' onClick={this._handleOk}>
              {i18n.t('common.add')}
            </Button>
            <Button type='primary' ghost onClick={this._handleCancel}>
              {i18n.t('common.cancel')}
            </Button>
          </div>
        </Modal>
      </div>
    )
  }
}

export default withTranslation()(InfoConference)
